    float w = 1.0*abs(cos( 0.11*iTime + 0.0 ));
	float h = 0.8*abs(cos( 0.13*iTime + 1.0 ));

    float d = sdRhombus(p, w, h);

    vec3 col = vec3(1.0,0.9,1.0) + sign(d)*vec3(-0.3,0.4,0.3);
