// Distance to a rounded box in the L-infinity metric space
//
// List of some other 2D distances in L-infinity:
//
// https://www.shadertoy.com/playlist/XXccDH


float sdRoundBox( in vec2 p, in vec2 ra, in float rb )
{
    p = abs(p) - ra;
    p = max(p,p.yx-rb);
    float a = 0.5*(p.x+p.y);
    float b = 0.5*(p.x-p.y);
    return a - sqrt(rb*rb*0.5-b*b);
}

