float sdEllipse( in vec2 p, in vec2 r )
{
    p = abs(p);
    p = max(p,(p-r).yx); // idea by oneshade

    float m = dot(r,r);
    float d = p.y-p.x;
    return p.x - (r.y*sqrt(m-d*d)-r.x*d) * r.x/m;
}

