// 3D Voronoi- (IQ)
float voronoi(vec3 p){

	vec3 b, r, g = floor(p);
	p = fract(p);
	float d = 1.; 
	for(int j = -1; j <= 1; j++)
    {
	    for(int i = -1; i <= 1; i++)
        {
		    b = vec3(i, j, -1);
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 0.0;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 1.;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
	    }
	}
	return d;
}

// fbm layer
float noiseLayers(in vec3 p) {

    vec3 pp = vec3(0., 0., p.z + iTime*.15);
    float t = 0.;
    float s = 0.;
    float amp = 1.;
    for (int i = 0; i < 5; i++)
    {
        t += voronoi(p + pp) * amp;
        p *= 2.;
        pp *= 1.5;
        s += amp;
        amp *= .5;
    }
    return t/s;
}

vec3 neb(in vec2 uv)
{
    float dd = length(uv*uv)*0.125;

	vec3 rd = normalize(vec3(uv.x, uv.y, 3.141592/8.));
    rd.xy *= rot(dd-iTime*.025);

	float c = noiseLayers(rd*2.25);
    float oc = c;
    c = max(c + dot(hash33(rd)*2. - 1., vec3(.006)), 0.);
    c = pow(c*1.45,3.5);    


    vec3 col =  vec3(0.55,0.8,0.35);
    vec3 col2 =  vec3(.85,0.85,1.4)*2.0;



    float pulse2 = voronoi(vec3((rd.xy*1.5),iTime*.255));
    float pulse = pow(oc*1.35,4.0);
    //pulse = clamp(pulse,0.0,2.0);
    col = mix(col,col2,pulse*pulse2)*c;
    return sqrt(col);
}

