    vec3 color = mix(background*0.45,background,d);


    d = smoothstep(0.0,blur, _d-0.3);
    color = mix(grey,color,d);


    d = _d-0.23;
    d = abs(d)-0.01;
    d = smoothstep(0.0,blur, d);
    color = mix(blue,color,d);

    d = _d-0.51;
    d = abs(d)-0.012;
    d = smoothstep(0.0,blur, d);
