    vec2 l4=vec2(0.);
    l4.x=step(0.,uv.x);
    l4.y=step(0.89,uv.y)-step(0.9,uv.y);

    vec2 l5=vec2(0.);
    l5.x=step(0.95,uv.x)-step(0.96,uv.x);
    l5.y=step(0.,uv.y);

    vec2 l6=vec2(0.);
    l6.x=step(0.05,uv.x)-step(0.06,uv.x);
    l6.y=step(0.8,uv.y);

    vec2 lt=vec2(0.);
    lt.x=step(0.9,1.-uv.x)-l6.x;
    lt.y=step(0.8,uv.y)-l4.y;

    vec2 rd=vec2(0.);
