    if(abs(id.y) < 1. && id.x >= 0. && id.x < 4.  && char < 200.){
        vec4 letter = texture(iChannel3,p + vec2(mod(char,16.),-floor(char/16.) )/16.);
        t = letter.w - opacity;
        if(abs(p.x-1./16./2.)>1./16./2.)
            t = 10e4;

        t /= s*10.1;
    } else {
