float zig(in vec2 uv) {
    return 1.-step(2.*abs(uv.y-0.5)-uv.x, 0.01);
}

float zag(in vec2 uv) {
    return 1.-step(2.*abs(uv.y-0.5)-(1.-uv.x), 0.01);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Tile the image into long skinny tiles
    vec2 uv = 15.* fragCoord/iResolution.x*(iResolution.y/iResolution.x);
    uv.x *= 2.5;
    vec2 pos = floor(uv);
    uv = fract(uv);


    // Starting from the regular color changey pretty thing.
    vec3 col = 0.5 + 0.35*cos(iTime * 0.5 - pos.x * 0.2 - pos.y +uv.xyx+vec3(0,1.3,3.5));

    vec3 triangle = vec3(0.);

    float offset = mod(pos.x+mod(pos.y,2.)*3.,6.);

    // The rows need to be offset from each other, and the arrows flip every three tiles

    if (offset <3.) {
        triangle = vec3(zig(uv));
    } else {
        triangle = vec3(zag(uv));
    }


    // That's it
    fragColor = vec4(max(triangle,col),1.0);
}

