// find point x on box(p, b, r) that maximizes dot(x, n)
vec2 support(vec2 p, vec2 b, mat2 r, vec2 n)
{
    return p + b * sign(r * n) * r;
}

// given a face with index (side, false) on box 1 or index (side, true) on box 2
// how far do you have to push/pull the boxes, so that they definitely don't intersect
void push1(
    inout vec4 C, vec2 uv,
    inout float d, inout vec2 normal, inout vec2 hit,
    vec2 p1, vec2 b1, mat2 r1,
    vec2 p2, vec2 b2, mat2 r2,
    int side,
    bool flip)
{
    if (flip)
    {
        swap(p1, p2);
        swap(b1, b2);
        swap(r1, r2);
    }
    const vec2[] sides = vec2[](vec2(-1,0),vec2(1,0),vec2(0,-1),vec2(0,1));
    vec2 n = sides[side] * r1;
    vec2 x = support(p2, b2, r2, -n);
    vec2 y = support(p1, b1, r1, n);
    d = dot(x - y, n);
