    C = blend(C, vec4(0,.5,0,1), abs(length(uv - x) - 10. * f));
}

// push boxes apart using the least distance traveled
void push2(
    inout vec4 C, vec2 uv,
    inout float d, inout vec2 normal, inout vec2 hit,
    vec2 p1, vec2 b1, mat2 r1,
    vec2 p2, vec2 b2, mat2 r2)
{
    d = -10.;
    vec4 c = vec4(0);
    for (int j=0 ; j<2 ; ++j)
    for (int i=0 ; i<4 ; ++i)
    {
        float di = -10.;
        vec2 ni, hi;
        vec4 ci = vec4(0);
        push1(ci, uv, di, ni, hi, p1, b1, r1, p2, b2, r2, i, j == 0);
        if (di > d)
        {
            d = di;
            hit = hi;
            normal = ni;
            c = ci;
        }
    }
    d = min(d, 0.); // only push, comment to pull
