const float pi = radians(180.);

float box(vec2 p, vec2 b)
{
    // https://iquilezles.org/articles/distfunctions
    vec2 d = abs(p) - b;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float line(vec2 p, vec2 a, vec2 b)
{
    b -= a;
    p -= a;
    return length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.));
}

float arrow(vec2 p, vec2 a, vec2 b)
{
    vec2 t = b - a;
    vec2 n = vec2(-t.y, t.x);
    float r = line(p, a, b);
    r = min(r, line(p, b - .2 * t - .1 * n, b));
    r = min(r, line(p, b - .2 * t + .1 * n, b));
    return r;
}

mat2 R(float a)
{
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

vec4 blend(vec4 C, vec4 c)
{
    return C + c * (1. - C.w);
}

float f;
vec4 blend(vec4 C, vec4 c, float r)
{
    return blend(C, c * smoothstep(f, 0., r));
}

