// Desmos graph: https://www.desmos.com/calculator/fd8azv2lgv

#define REPARAMETRIZE

// Constants
#define TAU 6.28318530718 // 2π
#define RHO 1.57079632679 // π/2

// Magic constant I haven't found a good way to autoselect yet
// Its a sort of scale factor
#define h 1.758

// Utilities
#define drawSDF(dist, col, opac) color = mix(color, col, smoothstep(unit, 0.0, dist) * (opac))

// SDFs
float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

float sdDisk(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

vec2 pSquircle(in float t, in float r, in float n) {
    vec2 p = vec2(cos(t), sin(t));
    return pow(abs(p), vec2(n)) * sign(p) * r;
}

// Approximation of arclength and inverse arclength
float gain(in float x, in float n) {
    float r = round(x);
    return abs(r - 0.5 * pow(2.0 * abs(r - x), n));
}

float arcSquircle(in float t, in float r, in float n) {
    t /= RHO;
    return (gain(fract(t), n) + floor(t)) * r * h;
}

float invArcSquircle(in float t, in float r, in float n) {
    t /= r * h;
    return (gain(fract(t), 1.0 / n) + floor(t)) * RHO;
}

float sdSquircle(in vec2 p, in float r, in float n) {
    p = abs(p);
    vec2 pn = pow(p, vec2(n));
    return abs((pn.x + pn.y - pow(r, n)) / length(pn / p * n));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 2.0;
    vec2 uv = (fragCoord - center) / iResolution.y * 2.0;
    float unit = 4.0 / iResolution.y;
    float time = 0.25 * iTime;
    vec3 color = vec3(1.0);

    float r = 0.75;
    float n = mix(1.0, 5.0, 0.5 + 0.5 * sin(0.25 * iTime));

    // This conversion bit was pulled out to simplify things
    // It is necessary for the parametrization to match the implicit form
    float pn = 2.0 / n;

    // Squircle
    float squircleAngle = atan(pow(abs(uv.y), 1.0 / pn) * sign(uv.y), pow(abs(uv.x), 1.0 / pn) * sign(uv.x));

    #ifndef REPARAMETRIZE
    float arclen = TAU * r;
    #else
    float arclen = arcSquircle(TAU, r, pn);
    squircleAngle = arcSquircle(squircleAngle, r, pn);
    #endif

    float dashesMask = mod(floor(squircleAngle / arclen * 50.0 - 2.5 * iTime), 2.0);
    drawSDF(sdSquircle(uv, r, n), vec3(0.0), dashesMask);

    // Points moving around the perimeter
    #ifndef REPARAMETRIZE
    for (float t=0.0; t < TAU; t += TAU / 50.0) {
        drawSDF(sdDisk(uv, pSquircle(t + time, r, pn), 0.025), vec3(1.0, 0.4, 0.5), 0.5 + 0.5 * sin(iTime));
    }
    #else
    for (float t=0.0; t < arclen; t += arclen / 50.0) {
        drawSDF(sdDisk(uv, pSquircle(invArcSquircle(t + time, r, pn), r, pn), 0.025), vec3(1.0, 0.4, 0.5), 0.5 + 0.5 * sin(iTime));
    }
    #endif

    //drawSDF(abs(uv.y - arcSquircle(uv.x, r, n)), vec3(0.0));
    //drawSDF(abs(uv.y - invArcSquircle(uv.x, r, n)), vec3(0.0));

    // Vignette (kinda)
    uv *= 0.5;
    float dd = dot(uv, uv);
    color -= 0.5 * dd * dd;

    fragColor = vec4(color, 1.0);
}

