    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(0,0,6.25);
    vec3 rd = normalize(vec3(uv,-1));
    // camera //
    mat2 rx =rot(-1.4);
    mat2 ry =rot(-.2);
    ro.zy*=rx;rd.zy*=rx;
