#define QAA	2                                     

const float	sideAngle = radians(35.0);
const float	horizontalAngle = radians(14.0);
const float	cameraSize      = 9.5;
const float	aspectRatio     = 0.75;
const float quiltColumns	= 8.0;
const float quiltRows		= 6.0;
const float nearClip        = 9.5;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4	accColor = vec4(0.);
    for( int m = 0; m < QAA; m++ )
    for( int n = 0; n < QAA; n++ )
    {
        // pixel coordinates
        vec2 o = (vec2(float(m), float(n)) / float(QAA) - 0.5) / iResolution.xy                                                                                 ;

		vec2	coord = (fragCoord / iResolution.xy  + o) * vec2(quiltColumns,quiltRows);
		vec2	fract = fract(coord);
		vec2	floor = floor(coord);
		float	imageId = floor.x + floor.y * quiltColumns;
		float	valueId = imageId / (quiltColumns * quiltRows - 1.);
		float	dist = (cameraSize / 2.) / tan(horizontalAngle / 2.);
		float	minCam = -(cameraSize / 2.) - tan(sideAngle / 2.) * dist;

		vec3	camPos = mix(vec3(minCam,0,dist),vec3(-minCam,0,dist),valueId);
		vec3	screenPos = vec3((fract.x-0.5)*cameraSize,(fract.y-0.5)*cameraSize/aspectRatio,0);

		vec3	dirRay = normalize(screenPos - camPos);
		vec4	color;
		mainVR(color, fragCoord, screenPos - vec3(0, 0, 9) - nearClip * dirRay, dirRay);
		accColor += clamp(color, 0.0, 1.0);
	}
	fragColor = accColor / float(QAA*QAA);
}

#define noiseFilter
#define Kernel 6

float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;
#ifdef noiseFilter
    float sigma = 3.0;
    float kernel[Kernel*2+1];
    for (int j = 0; j <= Kernel; ++j)
    {
        kernel[Kernel+j] = kernel[Kernel-j] = normpdf(float(j), sigma);
    }

    vec4 data = texture(iChannel1, uv);
    vec3 n0 = data.xyz;
    float depth0 = data.w;
    float z = 0.0;
    vec3 dir0 = normalize(target-camPos);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 right = normalize(cross(up, dir0));
    up = cross(dir0, right);
    vec3 rd = normalize(dir0 + right*uv.x + up*uv.y);
    vec3 p0 = camPos + depth0 * rd;

    for(int i = - Kernel; i <= Kernel; i++){
        for(int j = - Kernel; j <= Kernel; j++){
            vec2 uv2 =  uv + vec2(i, j)/iResolution.xy;
            data = texture(iChannel1,uv2);
            vec3 n = data.xyz;
            float depth = data.w;

            rd = normalize(dir0 + right*uv.x + up*uv.y);
            vec3 p = camPos + depth * rd;
            //same normal, on the same plane and not to far away
            if (dot(n, n0)>0.95 
                && abs(dot(n0, p - p0)) < 0.09
                && dot(p-p0, p-p0)<max(0.02, 0.02*depth0)){

                col += kernel[Kernel+j]*kernel[Kernel+i]*texture(iChannel0, uv2).rgb;
                z += kernel[Kernel+j]*kernel[Kernel+i];
            }
        }
     }
     col /= z;
     if (depth0 == -1.0) col = texture(iChannel0, uv).rgb;

    col = 1.0-exp(-0.5*col); 

#else
    col = texture(iChannel0, uv).xyz;

    col = 1.0-exp(-0.5*col); 
    col = pow(col, vec3(0.7)); 
#endif

    fragColor = vec4(col,1.0);
}

