#define MAXDEPTH 8


struct Mat{vec3 c, e;}; //color, emission

void initRandomGenerator(vec2 uv){
    seed = uint(uv.y*iResolution.x + uv.x) + uint(iFrame)*uint(iResolution.x)*uint(iResolution.y);
}

vec4 newDir(vec3 n){
    float teta = random()*2.0*PI;
    float z = random()*2.0-1.0;
    vec3 v = vec3(sqrt(1.0-z*z)*cos(teta), sqrt(1.0-z*z)*sin(teta), z);
    if(dot(n, v) < 0.0) return vec4(-v, 1.0);
    return vec4(v, 1.0);
}

vec4 newDirImpSamp(vec3 n){
     //dichotomie
    float total = texture(iChannel2, vec2(1.0, 1.0/float(downSample))).x*2.0;
    float goal = random();
    float y, val;
    vec2 data;
    float Min = 0.0;
    float Max = 1.0;
    for(int i = 0; i < 1024; i++){
      float Mid = 0.5*(Min+Max);
      vec2 data = texture(iChannel2, vec2(1.0, Mid/float(downSample))).xy;
      val = data.x/total;
      if(abs(val - goal) < 0.008){y = Mid; break;}
      (val < goal) ? Min = Mid : Max = Mid;

    }

    float weightY = data.y/total;

    //ont the x-axis at position y
    data = texture(iChannel2, vec2(1.0, y)/float(downSample)).xy;
    total = data.x*2.0;
    goal = random();
    float x;
    Min = 0.0;
    Max = 1.0;
    for(int i = 0; i < 1024; i++){
      float Mid = 0.5*(Min+Max);
      data = texture(iChannel2, vec2(Mid, y)/float(downSample)).xy;
      val = data.x/total;
      if(abs(val - goal) < 0.008){x = Mid; break;}
      (val < goal) ? Min = Mid : Max = Mid;
    }
    float weightX = data.y/total;


    float teta = x*2.0*PI;
    float z = y*2.0-1.0;
    vec3 v = vec3(sqrt(1.0-z*z)*cos(teta), z, sqrt(1.0-z*z)*sin(teta));
    if(dot(v, n) < 0.0)return newDir(n);

    float weightF = float(downSample*downSample)/(iResolution.x*iResolution.y);
    float weightG = weightX*weightY + 0.0001;
    float weight = weightF / weightG;
    return vec4(v, weight);

}

float intersect(vec3 ro, vec3 rd, out Mat obj, out vec3 n){

    float t = 1e7;
    obj = Mat(vec3(0.9), vec3(0.0));
    for(int i = 0; i < NUMBOX; i++){
        Box B = boxs[i];
        float t2 = box(ro, rd, B).x;

        if( t2 > 0.0 && t2 < t){
            t = t2;
            n = normal(ro + t*rd, B);

            if(i==3)obj = Mat(vec3(0.0, 0.9, 0.0), vec3(0.0));
            if(i==9)obj = Mat(vec3(0.9, 0.0, 0.0), vec3(0.0));
            if(i==11)obj = Mat(vec3(0.9, 0.9, 0.1), vec3(0.0));
            if(i!=3 && i!=9 && i!= 11)obj = Mat(vec3(0.9), vec3(0.0));
        }


    }
    if(t > 1e6){ obj = Mat(vec3(0.0), texture(iChannel1, rd).xyz);return -1.0;}
    return t;
}



vec3 march(vec3 ro, vec3 rd){
	vec3 col = vec3(0.0);
	vec3 mask = vec3(1.0);
    vec3 n;
	for (int depth = 0; depth < MAXDEPTH; ++depth) {
		Mat obj;
        float t = intersect(ro, rd, obj, n);

		ro = ro + t * rd;
#ifdef ImportanceSampling
        vec4 new = newDirImpSamp(n);
#else
		vec4 new = newDir(n);
#endif
        rd = new.xyz;
		col += mask * obj.e * new.w;
		mask *= obj.c * new.w;

        if(t<=0.0)break; // dont intersect anything
        if (dot(mask, mask)<0.000001)break; // have absorbed to much light that the ray is useless

	}
	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;
    initBoxs();
    initRandomGenerator(fragCoord);

    vec3 dir0 = normalize(target-camPos);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 right = normalize(cross(up, dir0));
    up = cross(dir0, right);
    vec3 rd = normalize(dir0 + right*uv.x + up*uv.y);


    vec3 col = march(camPos, rd);

    //mix with previous frames
    vec3 last = texture(iChannel0, fragCoord/iResolution.xy).xyz;
    float weight = 1.0/float(max(0, iFrame - frameDelay - 5) + 1);
    col = mix(last, col, weight);
    fragColor = vec4(col,1.0);
}

