void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy/float(downSample);
    vec3 col = vec3(0.5);

    // if all the work is already done
    if(iFrame - frameDelay > 5){
        col = texture(iChannel1, fragCoord/iResolution.xy).rgb;
        fragColor = vec4(col,1.0);
        return;
    }

    //get the values from the cube map
    if(iFrame - frameDelay < 4 && all(lessThanEqual(fragCoord, res))){
        vec2 uv = fragCoord/res;

        float teta = uv.x*2.0*PI;
        float z = uv.y*2.0-1.0;
        vec3 rd = vec3(sqrt(1.0-z*z)*cos(teta), z, sqrt(1.0-z*z)*sin(teta));
        col = textureLod(iChannel0, rd, log2(float(downSample))).rgb;
        col = vec3((col.r + col.g + col.b)/3.0);
        fragColor = vec4(col,1.0);
        return;
    }

    //sum the row values
    if(iFrame - frameDelay == 4){  // initialisation with delay if some frames are skip
        if(all(lessThanEqual(fragCoord, res))){
            vec2 invRes = 1.0/res;
            vec2 uv = fragCoord/res;
            col = texture(iChannel1, uv/float(downSample)).xyz;
            col.x = 0.0;
            for(int i = 0; i < 8000; i++){
                if(uv.x < 0.0) break;
                col.x += texture(iChannel1, uv/float(downSample)).x;
                uv.x -= invRes.x;
            }  
        }
        if(fragCoord.y <= res.y && fragCoord.x > iResolution.x - 3.0){
            vec2 invRes = 1.0/res;
            vec2 uv = vec2(1.0, fragCoord.y/res.y);
            for(int i = 0; i < 8000; i++){
                if(uv.x < 0.0) break;
                col += texture(iChannel1, uv/float(downSample)).xyz;
                uv.x -= invRes.x;
            }  

        }
        fragColor = vec4(col,1.0);
        return;
    }

    //sum the values of the final column
    if(iFrame - frameDelay == 5 && fragCoord.y <= res.y && fragCoord.x > iResolution.x - 3.0){
        vec2 invRes = 1.0/res;
        vec2 uv = vec2(1.0, fragCoord.y/res.y);
        col = texture(iChannel1, uv/vec2(1.0, float(downSample))).xyz;
        col.x = 0.0;
        for(int i = 0; i < 8000; i++){
            if(uv.y < 0.0) break;
            col.x += texture(iChannel1, uv/vec2(1.0, float(downSample))).x;
            uv.y -= invRes.y;
        }
        fragColor = vec4(col,1.0);
        return;
    }

    col = texture(iChannel1, fragCoord/iResolution.xy).rgb;
    fragColor = vec4(col,1.0);
}

