#define PI 3.14159265359

#define downSample 2   // error due to float numbers; better with 2, and 6 if you go full screen
#define frameDelay 0

//comment here to try without importance sampling
#define ImportanceSampling


#define NUMBOX 14
struct Box{vec3 up, down;};
Box[NUMBOX] boxs;


const vec3 camPos = vec3(0.5, 1.0, -1.8)*0.8;
const vec3 target = vec3(0.0, 0.0, 0.0);


// pseudo random numbers////////////////////////////////////////////////
uint seed = 0u;
void hash(){
    seed ^= 2747636419u;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
    seed ^= seed >> 16;
    seed *= 2654435769u;
}

float random(){
    hash();
    return float(seed)/4294967295.0;
}

void initBoxs(){
    seed = 34267554u;
    for(int idx = 0; idx < NUMBOX - 1; idx++){
        vec3 a = vec3(random(), random(), random());
        vec3 b = vec3(random(), random(), random());
        a = 2.0*a - 1.0;
        b = 2.0*b - 1.0;
        vec3 s = vec3(1.1);
        a *= s; b *= s;
        boxs[idx] = Box(max(a, b), min(a, b));
    }
    boxs[NUMBOX - 1] = Box(vec3(10.0, -0.95, 10.0), vec3(-10.0, -1.0, -10.0));
}
/////////////////////////////////////////////////////////////////////////

vec2 box(vec3 ro, vec3 rd, Box AABB){
        vec3 down = AABB.down;
        vec3 up = AABB.up;

        vec3 dirfrac = 1.0 / rd;
        float t1 = (down.x - ro.x)*dirfrac.x;
        float t2 = (up.x - ro.x)*dirfrac.x;
        float t3 = (down.y - ro.y)*dirfrac.y;
        float t4 = (up.y - ro.y)*dirfrac.y;
        float t5 = (down.z - ro.z)*dirfrac.z;
        float t6 = (up.z - ro.z)*dirfrac.z;

        float tmin = max(max(min(t1, t2), min(t3, t4)), min(t5, t6));
        float tmax = min(min(max(t1, t2), max(t3, t4)), max(t5, t6));

        // if tmax < 0, ray (line) is intersecting AABB, but the whole AABB is behind us
        if (tmax < 0.0) return vec2(-1.0);

        // if tmin > tmax, ray doesn't intersect AABB
        if (tmin > tmax) return vec2(-1.0);

        return vec2(tmin, tmax);
}

vec3 normal(vec3 pos, Box b){
    pos -= 0.5*(b.up + b.down);
    pos /= 0.5*abs(b.up - b.down);
    vec3 n = vec3(0.0);
    if(abs(pos.x)>0.995){n.x = pos.x;}
    if(abs(pos.y)>0.995){n.y = pos.y;}
    if(abs(pos.z)>0.995){n.z = pos.z;}
    n = normalize(n);
    return n;
}

