const float G2  = 98.00;
const float C3  = 130.81;
const float BF4 = 466.16;
const float C5  = 523.25;
const float D5  = 587.33;
const float EF5 = 622.25;
const float F5  = 698.46;
const float GF5 = 739.99;

float square_wave(float f, float x) {
    return floor(2.*floor(f*x)-floor(2.*f*x)+1.);
}

float triangle_wave(float f, float x) {
    return abs(fract(f*x)*2.-1.)*2.-1.;
}

float sine_wave(float f, float x) {
    return sin(6.2831*f*x);
}

float amog_note(float f, float x) {
    //float n = noise(x)*smoothstep(0.1,0.,x)*smoothstep(0.0,0.01,x)*0.1;
    float amp = smoothstep(0.0,0.01,x);
    amp *= sine_wave(7., x)*0.5+0.5;
    amp *= 0.25+0.75*smoothstep(0.12,0.1,x);
    amp *= smoothstep(0.3, 0.2, x);
    float w = sine_wave(f, x)*0.875+triangle_wave(f*2.,x)*0.125;
    return w * amp;
}

float amog_bass(float f, float x) {
    float amp = smoothstep(0.3,0.2, x);
    amp *= smoothstep(-0.00,0.01,x);
    amp *= 0.5 + 0.5 * smoothstep(0.1, 0.0, x);
    return (square_wave(f, x) + sine_wave(f*.5, x))*amp*0.5;
}

float soft_bass(float f, float x) {
    float amp = smoothstep(0.3,0.2, x);
    amp *= smoothstep(-0.00,0.01,x);
    amp *= 0.5 + 0.5 * smoothstep(0.1, 0.0, x);
    return (sine_wave(f*.5, x))*amp*0.5;
}

float clap(float x) {
    float amp = smoothstep(0.2,0.1,x);
    amp *= smoothstep(0.0,0.01,x);
    amp *= fract(x*20.);
    return (FBM21(x*50.).x*0.5+noise(x*200.))*amp*0.25;
}

float hihat(float x) {
    float amp = smoothstep(0.2,0.15, x);
    amp *= smoothstep(0.0,0.02, x);
    amp *= 0.25 + 0.75 * smoothstep(0.1,0.05, x);
    return (noise((x-.25)*(x-.25)*1000.)*0.5 + FBM21(x).x)*amp*0.5;
}

vec2 amog_drums(float t) {
    float eigh = fract(t*BPS*4.)/BPS;
    float trip = fract(t*BPS*3.)/BPS;
    float sext = fract(t*BPS*12.)/BPS*0.5;
    float fast = fract(t*BPS*18.)/BPS*0.5;

    float x = t*BPS-16.;
    float amp = 1.;
    amp *= 0.5 + 0.5 * smoothstep(0.125, 0.0, fract(x/2.));
    if(x > 0.) {x = fract(x*0.125)*8.;
        if(x > .5 && x < 1.) {amp *= smoothstep(1., 0.5, x); x = fast;} else 
        if(x > 2.5 && x < 2.75) {amp *= smoothstep(2.75, 2.5, x)*2.; x = fast;} else 
        if(x > 2.75 && x < 3.) {amp *= smoothstep(3., 2.75, x)*2.; x = fast;} else 
        if(x > 7. && x < 7.25) {x = sext;} else 
        if(x > 8. && x < 9.) {x = trip;} else 
        {x = eigh;}
    }
    float h = hihat(x) * amp;

    x = t*BPS-16.;
    amp = 0.5;
    if(x > 0.) {x = fract(x*0.25)*4.;
        if(x > .25 && x < .75) {x -= 0.25; x *= 2.;} else 
        if(x > 1. && x < 1.5) {x -= 1.; amp = 1.;} else 
        if(x > 1.75 && x < 2.25) {x -= 1.75; x *= 2.;} else 
        if(x > 2.25 && x < 2.75) {x -= 2.25; x *= 2.;} else 
        if(x > 3. && x < 3.5) {x -= 3.; amp = 1.;}
    }
    float c = clap(x) * amp;
    return vec2(
            h*0.5 + c
        );
}

vec2 amog_melody_synth(float t) {
    t = fract(t/16.*BPS)*16.*RBPS;
    return vec2(
         amog_note(C5, t-1.*RBPS)
        +amog_note(EF5, t-1.5*RBPS)
        +amog_note(F5, t-2.*RBPS)
        +amog_note(GF5, t-2.5*RBPS)
        +amog_note(F5, t-3.*RBPS)
        +amog_note(EF5, t-3.5*RBPS)
        +amog_note(C5, t-4.*RBPS)
        +amog_note(BF4, t-5.5*RBPS)
        +amog_note(D5, t-5.75*RBPS)
        +amog_note(C5, t-6.*RBPS)

        +amog_note(C5, t-9.*RBPS)
        +amog_note(EF5, t-9.5*RBPS)
        +amog_note(F5, t-10.*RBPS)
        +amog_note(GF5, t-10.5*RBPS)
        +amog_note(F5, t-11.*RBPS)
        +amog_note(EF5, t-11.5*RBPS)
        +amog_note(GF5, t-12.*RBPS)
        +amog_note(GF5, t-14.*RBPS)// them Triplets
        +amog_note(F5, t-14.33*RBPS)
        +amog_note(EF5, t-14.66*RBPS)
        +amog_note(GF5, t-15.*RBPS)
        +amog_note(F5, t-15.33*RBPS)
        +amog_note(EF5, t-15.66*RBPS)
        +amog_note(C5, t-16.*RBPS)
        )*0.5;
}

vec2 amog_melody_bass(float t) {
    t = fract(t/16.*BPS)*16.*RBPS;
    float bt = fract(t/8.*BPS)*8.*RBPS;
    return vec2(
        +soft_bass(C3, bt-0.*RBPS)
        +soft_bass(C3, bt-1.*RBPS)
        +soft_bass(C3, bt-2.*RBPS)
        +soft_bass(C3, bt-3.*RBPS)
        +soft_bass(C3, bt-4.*RBPS)
        +soft_bass(C3, bt-5.*RBPS)
        +soft_bass(C3, bt-6.*RBPS)
        +soft_bass(G2, bt-7.5*RBPS)
        +soft_bass(C3, bt-8.*RBPS)

        +amog_bass(G2, t-7.5*RBPS)
        +amog_bass(C3, t-8.*RBPS)
        +amog_bass(C3, t-0.*RBPS)
        );
}

vec2 mainSound( int samp, float time )
{
    vec2 s = vec2(amog_melody_bass(time)) * vec2(sin(time*BPS*0.25*PI), sin((time+1.)*BPS*0.25*PI));
    s += vec2(amog_melody_synth(time)) * vec2(0.5,1.0);
    s += vec2(amog_drums(time)) * vec2(1.0,0.25);
    return s;
}

