float sdEllipse(in vec2 p, in vec2 ab) {
    p = abs(p);
    if (ab.y > ab.x) p = p.yx, ab = ab.yx;

    vec2 pab = p * ab;
    float s1 = ab.x * ab.x - ab.y * ab.y;
    float s2 = length(pab);
    float offs = atan(pab.x / pab.y);

    float t = 1.1;
    for (int n=0; n < 10; n++) {
        float to = t + offs, t2 = 2.0 * t;
        float y = 0.5 * s1 * sin(t2) + s2 * cos(to);
        t -= y / (s1 * cos(t2) - s2 * sin(to));
        if (abs(y) < 1e-7 * (s2 + s1)) break;
    }

    pab = p / ab;
    return length(p - vec2(cos(t), sin(t)) * ab) * sign(dot(pab, pab) - 1.0);
}

// Trigless version (solving as a quartic in sin(t))
// Note that the final solution is asin(solveQuartic()) which allows
// the final position to be calculated without any trig
// (a*cos(asin(solveQuartic())), b*sin(asin(solveQuartic()))) =
// (a * sqrt(1 - solveQuartic()^2), b * solveQuartic())
float sdEllipse2(in vec2 p, in vec2 ab) {
    if (ab.y > ab.x) p = p.yx, ab = ab.yx;
    p = abs(p);

    // Line (mainly optimization, the solver handles this case just fine)
    if (abs(ab.x) < 1e-3 || abs(ab.y) < 1e-3) return length(vec2(p.x - clamp(p.x, -ab.x, ab.x), p.y));
    if (abs(ab.x - ab.y) < 1e-3) return length(p) - ab.x; // Circle (nab = a^2 - b^2 = 0 ---> sq/nab^2 = undefined)

    vec2 abab = ab * ab, pp = p * p * abab;
    float nab = abab.x - abab.y, sq = pp.x + pp.y;
    float k = 1.0 + pp.x / pp.y;

    float b = 2.0 / (sqrt(k / sq) * nab);
    float c = sq / (nab * nab);
    float e = c / k;
    c -= 1.0;

    float t = 0.73;
    for (int n=0; n < 12; n++) {
        float y = (((t + b) * t + c) * t - b) * t - e;
        t -= y / (((4.0 * t + 3.0 * b) * t + 2.0 * c) * t - b);
        if (abs(y) < 1e-7 * (b + e)) break;
    }

    vec2 pab = p / ab;
    return length(p - vec2(sqrt(1.0 - t * t), t) * ab) * sign(dot(pab, pab) - 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 10.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 9.0; // Slight lag to maintain visibility

    // Default before interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(6.0, 3.0);

    vec2 ab = abs(mouse);
    float d = uv.x < 0.0 ? sdEllipse(uv, ab) : sdEllipse2(uv, ab);
    d *= 0.2; // Undo UV scaling and shrink the isolines a bit for visual purposes

    // Nice colors from iq
    vec3 color = 1.0 - sign(d) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(d));
	color *= 0.8 + 0.2 * cos(140.0 * d);
	color = mix(color, vec3(1.0), smoothstep(0.015, 0.0, abs(d)));

    fragColor = vec4(color, 1.0);
}

