float zagRight(in vec2 _st) {
    return step(0.05, abs(_st.x) - abs(_st.y));
}

float zagLeft(in vec2 _st) {
    return step(0.1, (_st.x) - abs(_st.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/100.*(iResolution.y/iResolution.x);


    vec3 col = vec3(1.);

    vec2 pos = floor(st);
        st.x += iTime * 0.5 + pos.y;
       st = 4.*(fract(st )-0.5);

    if (mod(pos.x+pos.y,2.)>0.) {
        col *= zagLeft(st);
    } else {
        col *= zagRight(st);
    }



    col.r *= mod(pos.x, 3.4)/2. * (1. + 0.3* sin(iTime));
    col.g *= mod(pos.y, 3.1)/1.9 * (1. + 0.3* sin(iTime));
    col.b *= mod(pos.x + pos.y + 1., 3.7)/1.9* (1. + 0.5* sin(iTime+0.6));




    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    set_twist(iTime);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float speed_test = step(2.0 * iMouse.x - 0.5 * iResolution.x, fragCoord.x);

    vec3 orig = get_orig(iTime);
    vec3 dir = get_ray(fragCoord, iResolution.xy, iTime);


    vec4 col = texture(iChannel0,  0.25 * uv);

    float dist = float_from_col(col);

    float dist_refined = raymarch(orig, dir, speed_test * dist, 1.0e-4);


    if (dist_refined > MAX_DIST) {
      fragColor = vec4(0.0, 0.0, 0.0, 1.0);
      return;
    }


    vec3 loc = orig + dir * dist_refined;

    vec3 light = get_light(iTime);

    float dist_from_light = length(loc - light);

    vec2 light_uv = light_ray_to_uv(loc - light, iResolution.xy, iTime);
    float light_dist_from_tex = float_from_col(texture(iChannel1, light_uv));
    float dist_from_light_to_surf = raymarch(light, normalize(loc - light),
                                             speed_test * 0.5 * light_dist_from_tex, 1.0e-4);
    float subsurf = max(0.0, dist_from_light - dist_from_light_to_surf);


    vec3 spectrum =  60.0 * vec3(0.05, 0.25, 0.85);


    float metal = smoothstep(0.3, 0.2, engine_check(loc));

    if (metal > 0.5) {
      spectrum = spectrum * 0.001;
    }

    vec3 subsurf_bright = spectrum / (spectrum + subsurf);

    vec3 normal = sdf_normal(loc);
    vec3 bounce = normalize(reflect(dir, normal));

    float dot_light = dot(normalize(light - loc), bounce);
    vec3 refl_bright = smoothstep(vec3(0.8, 0.85, 0.9), vec3(1.0), vec3(dot_light));
    float diffuse = 0.2 + max(0.0, dot(normalize(light - loc), normal));

    if (metal > 0.5) {
      diffuse = 0.2 + 1.0 * max(0.0, dot_light); //  + 0.1 * sin(20.0 * bounce.y);
    }

    vec3 color1 = 0.75 * subsurf_bright * diffuse + 0.5 * refl_bright + 0.15 * subsurf_bright;
    vec3 color2 = 0.75 * smoothstep(0.95, 1.0, refl_bright) + 0.25 * (0.5 + 0.5 * bounce);

    float material = smoothstep(0.05, 0.1, 2.0 * cockpit_check(loc)); 
    // material = 0.0;
    // Output to screen
    fragColor = vec4(mix(color2, color1, material) + mix(vec3(1., 0.5, 0.), vec3(0.), speed_test), 1.0);
   // fragColor = fragColor + 200.0 * texture(iChannel1,  0.5 * uv);
}

