// more on https://www.shadertoy.com/view/XsjfDD
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Set background color - blue
    //fragColor = vec4(0.0, 0.0, 1.0, 1.0);

    //Set background color - black
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    // Normalized pixel coordinates (from 0 to 1) and camera distance
    float distance = 3.0;
    vec2 uv = distance * (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.xx;
    // Get mouse position between 0 and 1 
    float rot_x = (iMouse.x / iResolution.x) * 3.1415*2.0;
    float rot_y = (iMouse.y / iResolution.y) * 3.1415;
    // Set camera rotation: left - right with rot_x, up - down with rot_y
    vec3 camera_rotation = vec3(cos(rot_x), cos(rot_y), sin(rot_x));

    vec3 f = -normalize(camera_rotation);     // Camera position forward
    vec3 r = normalize(vec3(-f.z, 0.0, f.x)); // Camera right 
    vec3 u = cross(r, f);                     // Camera to normalize image
    vec3 rd = normalize(f + uv.x * r + uv.y * u);// Render formula for fragColor output

    // Output to screen
    fragColor += texture(iChannel0, rd);

}

vec3 solveQuadratic(in float a, in float b, in float c) {
    if (abs(a) < 1e-6) return vec3(-c / b, 0, 1);
    float h = b * b - 4.0 * a * c;
    if (h < 0.0) return vec3(0.0);
    return vec3((vec2(-1.0, 1.0) * sqrt(h) - b) * 0.5 / a, 2.0);
}

vec4 solveCubic(in float a, in float b, in float c, in float d) {
    if (abs(a) < 1e-9) return vec4(solveQuadratic(b, c, d), 0.0).xywz;
    float u = b / (3.0 * a);

    float p = (c - b * u) / a;
    float q = (d - (c - 2.0 * b * b / (9.0 * a)) * u) / a;
    if (abs(p) < 1e-3) return vec4(-sign(q) * pow(abs(q), 1.0 / 3.0) - u, 0, 0, 1);

    if (4.0 * p * p * p + 27.0 * q * q > 0.0) {
        float ip = 3.0 / abs(p), sp = sqrt(ip), k = 0.5 * q * ip * sp, x;
        if (p > 0.0) x = -2.0 / sp * sinh(asinh(k) / 3.0) - u;
        else x = -2.0 / sp * cosh(acosh(abs(k)) / 3.0) * sign(q) - u;
        return vec4(x, 0, 0, 1);
    }

    float m = sqrt(-p / 3.0);
    float x1 = -2.0 * m * sin(asin(1.5 * q / (p * m)) / 3.0);

    float h = sqrt(-3.0 * x1 * x1 - 4.0 * p);
    float x2 = 0.5 * (h - x1), x3 = 0.5 * (-h - x1);

    return vec4(x1 - u, x2 - u, x3 - u, 3);
}

float evalDist(in vec2 p, in float t, in float tmin, in float tmax, in bool limit, in vec2 a, in vec2 b, in vec2 c, in vec2 d) {
    if (limit) t = clamp(t, tmin, tmax);
    vec2 toCurve = p - (((a * t + b) * t + c) * t + d);
    return max(abs(toCurve.x), abs(toCurve.y));
}

float sdCubic(in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d, in float tmin, in float tmax, in bool limit) {
    // Solving for the global minimum of max(|a.x*t^3 + b.x*t^2 + c.x*t + d.x - p.x|, |a.y*t^3 + b.y*t^2 + c.y*t + d.y - p.y|)
    // Minima and maxima are either intersections of the two with different combinations of signs where the derivative jumps from one to the other
    // or the minima and maxima of one of the cubics (sign flip due to abs() does not change them)
    vec2 dp = d - p;

    // |a.x*t^3 + b.x*t^2 + c.x*t + d.x - p.x| = |a.y*t^3 + b.y*t^2 + c.y*t + d.y - p.y|
    // All combinations of signs on both sides degenerate into either +/+ or +/-
    vec4 minMax1 = solveCubic(a.x - a.y, b.x - b.y, c.x - c.y, dp.x - dp.y);
    vec4 minMax2 = solveCubic(a.x + a.y, b.x + b.y, c.x + c.y, dp.x + dp.y);

    // Minima and maxima of both cubics
    // Remember that they CAN have a global minimum because of abs() forcing them
    // to bounce back up when they go below the x axis
    vec3 minMax3 = solveQuadratic(3.0 * a.x, 2.0 * b.x, c.x);
    vec3 minMax4 = solveQuadratic(3.0 * a.y, 2.0 * b.y, c.y);

    // Find minimum
    float dist = evalDist(p, minMax1[0], tmin, tmax, limit, a, b, c, d);
    for (int n=1; n < int(minMax1.w); n++) dist = min(dist, evalDist(p, minMax1[n], tmin, tmax, limit, a, b, c, d));
    for (int n=0; n < int(minMax2.w); n++) dist = min(dist, evalDist(p, minMax2[n], tmin, tmax, limit, a, b, c, d));
    for (int n=0; n < int(minMax3.z); n++) dist = min(dist, evalDist(p, minMax3[n], tmin, tmax, limit, a, b, c, d));
    for (int n=0; n < int(minMax4.z); n++) dist = min(dist, evalDist(p, minMax4[n], tmin, tmax, limit, a, b, c, d));

    return dist;
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    float time = iTime * 0.5;

    // Default before user interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(2.0, 2.0 * sin(time)) * cos(time);

    float t1 = iTime * 0.5, t2 = iTime, t3 = iTime * 1.25;

    float c1 = cos(t1), s1 = sin(t1);
    float c2 = cos(t2), s2 = sin(t2);
    float c3 = cos(t3), s3 = sin(t3);

    vec2 v1 = vec2(c2, s3 * 1.5 - 0.25);
    vec2 v2 = vec2(-2.0, 0.5);
    vec2 v3 = vec2(2.0, 0.5);
    vec2 v4 = vec2(s2, c3 * 1.5 - 0.25);

    // To power basis
    vec2 a = v4 + 3.0 * (v2 - v3) - v1;
    vec2 b = 3.0 * (v1 - 2.0 * v2 + v3);
    vec2 c = 3.0 * (v2 - v1);
    vec2 d = v1;

    float dist = sdCubic(uv, a, b, c, d, 0.0, 1.0, true);

    // Nice colors from iq
    vec3 color = 1.0 - sign(dist) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(dist));
	color *= 0.8 + 0.2 * cos(140.0 * dist);
	color = mix(color, vec3(1.0), smoothstep(unit, 0.0, abs(dist)));

    // Control points
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - v1) - 0.05));
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - v4) - 0.05));

    float mDist = sdCubic(mouse, a, b, c, d, 0.0, 1.0, true);
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - mouse) - 0.03));
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, abs(max(abs(uv.x - mouse.x), abs(uv.y - mouse.y)) - mDist) - 0.005));

    fragColor = vec4(color, 1.0);
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1

float fprand(vec2 uv, float f){
    return sin(uv.y*f)+cos(uv.x*f);
}

float noise(in vec2 uv)
{
    return sin(uv.x)+cos(uv.y);
}


#define OCTAVES 8
float fbm(in vec2 uv)
{
    //this function generates the terrain height
    uv /= 6.0;
    float value = 0.;
    float amplitude = 1.;
    float freq = 0.8;

    for (int i = 0; i < OCTAVES; i++)
    {
        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value = max(value,value+(.25-abs(noise(uv * freq)) * amplitude));

        amplitude *= .55;

        freq *= 1.75+value;

        uv = uv.yx;
    }

    return 1.-value;
}



struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 3.0;
    return fbm(uv/factor)*factor;
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*iTime);
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 0.05;
    #endif

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

