vec3 hexToRgb(int color)
{
    float rValue = float(color / 256 / 256);
    float gValue = float(color / 256 - int(rValue * 256.0));
    float bValue = float(color - int(rValue * 256.0 * 256.0) - int(gValue * 256.0));
    return vec3(rValue / 255.0, gValue / 255.0, bValue / 255.0);
}

const int COLORS_PER_GRADIENT = 5;
vec3 gradientColorAt(vec4[COLORS_PER_GRADIENT] gradient, float pct){
    pct = clamp(pct, 0., 1.);
    for (int i = 0; i < COLORS_PER_GRADIENT-1; i++){
        // find which two positions pct is between
        if (gradient[i].a <= pct && gradient[i+1].a >= pct){
            // normalize the position between the two closest color positions
            float norm = map(pct, gradient[i].a, gradient[i+1].a, 0., 1.);
            // mix the two neighbouring colors in the usual way
            return mix(gradient[i].rgb, gradient[i+1].rgb, norm);
        }
    }
    // throw red exception 
    return vec3(1, 0, 0);
}


