#define SCALE 4.0

// https://www.shadertoy.com/view/4djSRW
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

// Random cycling points
vec2 getPoint(in vec2 cell) {
    float freq = Hash11(dot(cell, vec2(393.84, 673.48))) * 10.0 - 5.0;
    float phase = Hash11(dot(cell, vec2(348.46, 183.37)));
    float amp = Hash11(dot(cell, vec2(275.35, 741.69)));
    float t = iTime * freq + phase;
    return cell + 0.5 + 0.5 * vec2(cos(t), sin(t)) * amp;
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

// Get UV coordinates in a bilinear patch (requires solving a quadratic)
float cross2D(in vec2 a, in vec2 b) { return a.y * b.x - a.x * b.y; }
bool iBilinear(in vec2 xy, in vec2 a, in vec2 b, in vec2 c, in vec2 d, out vec4 uv) {
    vec2 p = a - b + c - d;
    vec2 q = b - a, r = d - a;

    float c1 = cross2D(r, p);
    float c2 = cross2D(r, q) + cross2D(a, p) + cross2D(p, xy);
    float c3 = cross2D(a, q) + cross2D(q, xy);

    float discr = c2 * c2 - 4.0 * c1 * c3;
    if (discr > 0.0) {
        uv.yw = (vec2(-1.0, 1.0) * sqrt(discr) - c2) / c1 * 0.5;
        uv.xz = (xy.x - a.x - r.x * uv.yw) / (p.x * uv.yw + q.x);
        return true;
    }

    return false;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * SCALE;
    float unit = 2.0 * SCALE / iResolution.y;
    vec3 color = vec3(0.0);

    vec2 cell = floor(uv);
    vec2 centerPoint = getPoint(cell);
    for (int i=-1; i <= 1; i++) {
        for (int j=-1; j <= 1; j++) {
            if (abs(i) != abs(j)) {
                // Draw lines to the right, left, top, and bottom cell points
                vec2 edgePoint = getPoint(cell + vec2(i, j));
                color += smoothstep(unit, 0.0, sdLine(uv, centerPoint, edgePoint));
            }

            if (i <= 0 && j >= 0) {
                vec4 patchUv;

                // Cells and their vertices
                vec2 c1 = cell + vec2(i    , j - 1), v1 = getPoint(c1);
                vec2 c2 = cell + vec2(i + 1, j - 1), v2 = getPoint(c2);
                vec2 c3 = cell + vec2(i + 1, j    ), v3 = getPoint(c3);
                vec2 c4 = cell + vec2(i    , j    ), v4 = getPoint(c4);

                // Draw the current warped cell
                if (iBilinear(uv, v1, v2, v3, v4, patchUv)) {
                    if (all(lessThan(abs(patchUv.zw - 0.5), vec2(0.5)))) {
                        vec2 texUv = (iResolution.y / SCALE * (c1 + patchUv.zw) + center) / iResolution.xy;
                        color += texture(iChannel0, texUv).rgb;
                    }
                }
            }
        }
    }

    fragColor = vec4(color, 1.0);
}

