float gaussian_avg(vec2 xy, float heat) { //A trick to approximate a guassian
    float neighbors = -3.*heat; // (black magic)
    for(float i=-1.; i<=1.; i+=1.) {
        for(float j=-1.; j<=1.; j+=1.) {
        	vec2 offset = vec2(i,j) / iResolution.xy;
            float m = 3. - abs(i) - abs(j); // (black magic)
        	neighbors += m * texture(iChannel0, xy+offset).a; // Accumulate the result							 
        }
    }
    return neighbors / 12.; // (black magic) Turn the sum into an avg
}

