/** INPUT PARAMETERS */

#define scale 0.2
#define nb_octaves 5

/** @brief Generates a random 2D vector */
vec2 random_2_2(vec2 p)
{
    p = p*mat2(127.1,311.7,269.5,183.3);
	p = -1.0 + 2.0 * fract(sin(p)*43758.5453123);
	return sin(p*6.283);
}

/** @brief improved version of the smoothstep fucntion which garantees 
    zero 1st and 2nd order derivative at its end points (0 and 1) */
float smootherstep(float t) 
{ 
    return t * t * t * (t * (t * 6. - 15.) + 10.); 
}

/** @brief gradient noise */
float gradientNoise(vec2 pos)
{
    vec2 fr = fract(pos);
    vec2 fl = floor(pos);

    vec2 AA= fl;
    vec2 AB= fl  + vec2(1,0);
    vec2 BA= fl  + vec2(0,1);
    vec2 BB= fl  + vec2(1,1);

    // we sample random vectors on a regular grid latices
    //
    //  AA-----------AB
    //   |           |
    //   |           |
    //   |    pos    |
    //   |           |
    //   |           |
    //  BA-----------BB
    vec2 rAA = random_2_2(AA);
    vec2 rAB = random_2_2(AB);
    vec2 rBA = random_2_2(BA);
    vec2 rBB = random_2_2(BB);

    // we compute de dot product between 
    // the unit vectors
    // and the vector from the sample 
    // to the po regular grid position
    float dAA = dot(rAA, fr);
    float dAB = dot(rAB, fr-vec2(1,0));
    float dBA = dot(rBA, fr-vec2(0,1));
    float dBB = dot(rBB, fr-vec2(1,1));

    // bilinear interpolation of the value to reduce the grid 
    // artifact we smooth the interpolation weight.
    fr.x = smootherstep(fr.x);
    fr.y = smootherstep(fr.y);

    float d_AA_AB = mix(dAA,dAB,fr.x);
    float d_BA_BB = mix(dBA,dBB,fr.x);

    return mix(d_AA_AB,d_BA_BB,fr.y) +0.5;
}

/** @brief fractional brownian motion */
float fbm(vec2 uv)
{
    float noise = 0.0;
    float weights = 0.0;

    // sum of the value octaves
    for(int i = 0; i< nb_octaves; i++){
        // octave frequency
        float freq = 0.0625 *  pow(2.0,float(i+1));
        // octave weight
        float weight = pow(2.0,-float(i+1));

        // sum of the octave times the weight
        noise += gradientNoise(uv* freq)*weight;
        // sum of the weight
        weights += weight;
    }

    // return the sum of octaves divided by the sum of the weight
    return noise /weights;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // we sample the fractional brownian motion at a point
    // we shift it from the origin to avoid artefacts
    float value = fbm(fragCoord * scale  + vec2(97,89)) ;

    fragColor = vec4(value);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 C = iMouse.xy / iResolution.xy;
    vec2 uv = fragCoord / iChannelResolution[0].xy;
    float m = float(iFrame % R) / float(R);  
    uv -= C;
    uv.x *= iResolution.x / iResolution.y;
    uv = uv / (1.0 + 0.1*dot(uv,uv));
    uv.x /= iResolution.x / iResolution.y;
    uv *= exp(mix(log(1.0),log(0.5),m));
    float f = 0.004;
    vec2 uv3 = uv * exp(0.0) + C;
    vec2 uv2 = uv * exp(-f) + C;
    vec2 uv1 = uv * exp(-f*2.0) + C;
    float r = texture(iChannel0, uv1).r*0.5+0.5;
    float g = texture(iChannel0, uv2).r*0.5+0.5;
    float b = texture(iChannel0, uv3).r*0.5+0.5;    
    fragColor = vec4(pow(vec3(r,g,b), vec3(0.5)),1.0);
}

