    p.yz *= rot(sin(iTime));

    //d = min(length(p.xy),length(p.zx));
    //d = min(d,length(p.zy)) - 0.1;

    float e = env;
    float db = sdBox(p,vec3(0.5) - sin(e)*0.1) - 0.07; 
