    //d = abs(d) - 0.001;
    return d;
}


vec3 getNormal(vec3 p){
    vec2 t = vec2(0.004,0.);
    return normalize( 
        map(p) - vec3(
            map(p - t.xyy),
            map(p - t.yxy),
            map(p - t.yyx)
        )
    );
}
