            chrab += sin(dot(rd,reflect(rd,n))*10.);
            float rf = pow(max(dot(n,normalize(rd-normalize(vec3(1)))),0.), 2. )*4.;
            rf = pow(1.-max(dot(-rd,n),0.),5.);
