int idObj;
const float pi = 3.1415927;

const int nObj = 97;

const ivec3 nSphObjD = ivec3 (6, 3, 3);
const int nSphObj = nSphObjD.x * nSphObjD.y * nSphObjD.z;
const int nSphTot = nObj * nSphObj;
const int nPre = 2;

vec3 GetR (int k)
{
  return Loadv4 (nPre + 4 * k).xyz;
}

vec4 GetQ (int k)
{
  return Loadv4 (nPre + 4 * k + 2);
}

vec4 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp, fId, u;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    u = (v + dn) * rd;
    fId = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    qBlk = vec2 (dot (u.zxy, fId), dot (u.yzx, fId));
  }
  return vec4 (dMin, fId);
}

float BlkHitSh (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) dMin = dn;
  return dMin;
}

float ObjHit (vec3 ro, vec3 rd)
{
  mat3 mRot;
  vec4 cdn4;
  vec3 rm, rom, rdm;
  vec2 qBlkN;
  float dMin;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nObj; n ++) {
    rm = GetR (n);
    mRot = QtToRMat (GetQ (n));
    rom = (ro - rm) * mRot;
    rdm = rd * mRot;
    cdn4 = BlkHit (rom, rdm, bSize);
    if (cdn4.x < dMin) {
      dMin = cdn4.x;
      fcBlk = cdn4.yzw;
      qBlkN = qBlk;
      idObj = n;
    }
  }
  qBlk = qBlkN;
  return dMin;
}

float ObjHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 mRot;
  vec3 rm, rom, rdm;
  float dMin;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nObj; n ++) {
    rm = GetR (n);
    mRot = QtToRMat (GetQ (n));
    rom = (ro - rm) * mRot;
    rdm = rd * mRot;
    dMin = min (dMin, BlkHitSh (rom, rdm, bSize));
  }
  return smoothstep (0.5 * rng, rng, dMin);
}

vec3 ObjCol (vec3 rd, float sh)
{
  mat3 mRot;
  vec3 q, vn, col;
  float ep;
  vn = fcBlk;
  mRot = QtToRMat (GetQ (idObj));
  col = HsvToRgb (vec3 (mod (- dot (rd, mRot * vn), 1.), 0.5, 1.));
  ep = smoothstep (0., 0.2, PrBox2Df (qBlk, ((abs (vn.x) == 1.) ? bSize.zy :
     ((abs (vn.y) == 1.) ? bSize.xz : bSize.yx)) - 0.2));
  if (abs (vn.y) == 1.) ep = max (ep, 1. - smoothstep (0., 0.1,
     abs (PrBox2Df (qBlk, 0.5 * bSize.xz)) - 0.05));
  col *= 1. - 0.3 * ep;
  if (ep > 0.) {
    q = vec3 (qBlk, 0.);
    if (abs (vn.x) == 1.) q = q.zxy;
    else if (abs (vn.y) == 1.) q = q.xzy;
    vn = VaryNf (32. * q, vn, 0.7);
  }
  vn = mRot * vn;
  col = col * (0.2 +  0.8 * sh * max (dot (vn, ltDir), 0.)) +
     0.2 * sh * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj, sh;
  bool isBg;
  bSize = 0.5 * blkGap * (vec3 (nSphObjD) - 1.) + 0.4;
  dstObj = ObjHit (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    isBg = false;
  } else {
    if (rd.y < 0.) ro += ((0.5 - ro.y) / rd.y) * rd;
    isBg = true;
  }
  if (! isBg || isBg && rd.y < 0.) sh = 0.6 + 0.4 * ObjHitSh (ro + 0.01 * ltDir, ltDir, 60.);
  if (! isBg) col = ObjCol (rd, sh);
  else if (rd.y < 0.) {
    vn = vec3 (0., 1., 0.);
    col = mix (vec3 (0.8, 0.8, 0.75), vec3 (0.8, 0.8, 0.85),
       smoothstep (0., 0.1, Fbm2 (0.1 * ro.xz) - 0.4));
    col *= 0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.);
    col *= 1. - 0.9 * smoothstep (0.9, 1., length (ro.xz) / dstFar);
  } else col = vec3 (0.1);
  if (isBg && rd.y < 0.) {
    rd = reflect (rd, vn);
    ro += 0.01 * rd;
    dstObj = ObjHit (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      col = mix (col, ObjCol (rd, 1.), 0.3);
    }
  }
  return clamp (col, 0., 1.);
}

#define AA    0

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat, mPtr;
  vec3 col, rd, ro;
  vec2 canvas, uv;
  float az, el, asp, zmFac, sr, h;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (0);
  blkGap = stDat.yzw;
  stDat = Loadv4 (1);
  mPtr.xyz = stDat.xyz;
  nRun = stDat.w;
  dstFar = 300.;
  az = -0.2 * pi;
  el = -0.12 * pi;
  if (mPtr.z > 0. && Minv2 (mPtr.xy + 0.05 * vec2 (1. / asp, 1.) - 0.5) < 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  el = clamp (el, -0.48 * pi, -0.05 * pi);
  vuMat = StdVuMat (el, az);
  ro = vec3 (0., 0., -120.);
  h = 0.;
  for (int n = VAR_ZERO; n < nObj; n ++) h += GetR (n).y;
  ro.y = min (h / float (nObj), 10.);
  ro = vuMat * ro;
  zmFac = 5.3;
  ltDir = vuMat * normalize (vec3 (0.6, 0.5, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (mPtr.z > 0. && Minv2 (uv - vec2 (asp, 1.)) > -0.1) col = mix (col, vec3 (0.5, 1., 1.), 0.3);
  fragColor = vec4 (col, 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 256.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

