float px_dotsize = 20.0; // almost const, computed once at start of main
const float dots_per_screen = 100.0;
vec2 edge_size = vec2(0.01);

mat2 makeTwist(in float theta) {
  float st = sin(theta);
  float ct = cos(theta);
  return mat2(ct, st, -st, ct);
}

vec2 dotCenter(in vec2 uv, in mat2 twist) {
  vec2 xformed = twist * uv;
  xformed = xformed - mod(xformed, vec2(px_dotsize));
  xformed = xformed + 0.5 * px_dotsize;
  return transpose(twist) * xformed;
}

float dotNess(in vec2 uv, in vec2 dot_center, in float val) {
  float rad = 0.6 * px_dotsize * sqrt(smoothstep(0.0, 1.0, val)); // val between 0 and 1
  float len = length(uv - dot_center);
  float out_val = (rad-len);
  return clamp(out_val, 0.0, 1.0);
}

vec3 dotCmy(in vec2 uv) {
  mat2 twist = makeTwist(0.1);
  vec2 center = dotCenter(uv, twist);
  float val = 1.0 - textureLod(iChannel0, center/iResolution.xy, 2.0).r;
  float cyan = dotNess(uv, center, val);
  twist = makeTwist(-0.75);
  center = dotCenter(uv, twist);
  val = 1.0 - textureLod(iChannel0, center/iResolution.xy, 2.0).g;
  float magenta = dotNess(uv, center, val);
  twist = makeTwist(0.4);
  center = dotCenter(uv, twist);
  val = 1.0 - textureLod(iChannel0, center/iResolution.xy, 2.0).b;
  float yellow = dotNess(uv, center, val);
  return vec3(cyan, magenta, yellow);
}

float edgeness(in vec2 uv) {
  float f = dot(texture(iChannel0, uv).rgb, vec3(1.0));
  vec2 grad = vec2(dot(texture(iChannel0, uv + edge_size * vec2(1.0, 0.0)).rgb, vec3(1.0)),
                   dot(texture(iChannel0, uv + edge_size * vec2(0.0, 1.0)).rgb, vec3(1.0)))
              - f;
  // grad = grad / min(edge_size.x, edge_size.y);
  return length(grad);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    px_dotsize = max(iResolution.x, iResolution.y) / dots_per_screen;
    edge_size = edge_size.x * iResolution.x / iResolution.xy;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 cmy = dotCmy(fragCoord);

    float edgeblack = smoothstep(0.75, 0.42, edgeness(uv));

    // Output to screen
    fragColor = vec4(edgeblack * (1.0 - cmy),1.0);
}

#define BW 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = step(texture(iChannel1, uv).rgb, texture(iChannel0, uv).rgb);

#if BW
    float bw_col = step(dot(vec3(1.0), texture(iChannel1, uv).rgb),
                        dot(vec3(1.0), texture(iChannel0, uv).rgb));
    col = vec3(bw_col);
#endif

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 c, vec2 p)
{
  for(ivec4 b = ivec4(c -= c); (b.x^b.y&b.z)%99 > b.z-9;)
    b = ivec4((p/5e2-.5)*c.a + iTime/.1, ++c);
  c /= 99.;
}

