float DistanceToLineSegment(vec2 point, vec2 start, vec2 end) {
    float A = point.x - start.x;
    float B = point.y - start.y;
    float C = end.x - start.x;
    float D = end.y - start.y;

    float dot1 = A * C + B * D;
    float len_sq = C * C + D * D;
    float param = -1.0;
    if (len_sq != 0.0) {  param = dot1 / len_sq; }

    float xx, yy;

    if (param < 0.0) {
        xx = start.x;
        yy = start.y;
    }

    else if (param > 1.0) {
        xx = end.x;
        yy = end.y;
    }

    else {
        xx = start.x + param * C;
        yy = start.y + param * D;
    }

    float dx = point.x - xx;
    float dy = point.y - yy;
    return sqrt(dx * dx + dy * dy);
}

float SDFCircle( in vec2 coords, in vec2 offset )
{
    coords -= offset;
    float v = coords.x * coords.x + coords.y * coords.y - EDGE*EDGE;
    vec2  g = vec2(2.0 * coords.x, 2.0 * coords.y);
    return v / length(g); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 act_coord = ((fragCoord.xy / iResolution.xy) - vec2(0.25,0.5));
    float aspectRatio = iResolution.x / iResolution.y;
    act_coord.x *= aspectRatio;

    float dist;

    vec2 control_points[max_count];

    vec4 state = BufA(statePos);
    vec3 color = vec3(1.0,1.0,1.0);

    for (int i = 0; i < max_count; ++i) {
        float f = float(i);
        if (f >= state.y) { break; }

        vec2 center = BufA(f).xy;
        center = ((center.xy / iResolution.xy) - vec2(0.25,0.5));
        center.x *= aspectRatio;

        control_points[i] = center;
    }


    float t = fract(iTime / SLOW_DOWN);

    if (int(iTime / SLOW_DOWN) % 2 == 1 && BACKWARDS_ANIMATION) {
        t = 1.0 - t;
    }

    vec2 test = vec2(-1.0);

    // draw bezier curve using linear-time algorithm
    if (SHOW_BLACK_LINE) {
        float end_algorithm = t;
        float drawing_steps = float(DRAWING_STEPS);

        if(ALWAYS_SHOW_BEZIER_CURVE) { end_algorithm = 1.0; }
        else { drawing_steps *= t; } // without this big DRAWING_STEPS and small t couses visual glitch (i don't know why :D)

        float increaser = end_algorithm / drawing_steps;

        end_algorithm += 0.001; // without it some small values of CASTELJAUS_STEPS couses ending curve before endpoint
                                // (i don't know why)

        vec2 prev_point = control_points[0];

        for (float i = 0.0; i <= end_algorithm; i += increaser) {
            float i2 = 1.0 - i;

            float h = 1.0;
            float u = (1.0 - i);
            int n = max_count - 1;
            int n1 = n + 1;
            vec2 Q = control_points[0];

            if (i != 0.0) {
                if (i <= 0.5) {
                    u = i / u;
                    for (int k = 1; k <= n; ++k) {
                        float prev_h = h;
                        h = h * u * (float(n1) - float(k));
                        h = h / (float(k) + h);
                        float h1 = (h / prev_h) * ((float(k) * (1.0 - i)) / (i * float(n - k + 1)));
                        Q = h1 * Q + h * control_points[k];
                    }
                }
                else {
                    u = u / i;
                    for (int k = 1; k <= n; ++k) {
                        float prev_h = h;
                        h = h * (float(n1) - float(k));
                        h = h / (float(k) * u + h);
                        float h1 = (h / prev_h) * ((float(k) * (1.0 - i)) / (i * float(n - k + 1)));
                        Q = h1 * Q + h * control_points[k];
                    }
                }
            }

            vec2 point = Q;

            dist = DistanceToLineSegment(act_coord, prev_point, point) * 2.0;

            if (dist < EDGE + SMOOTH)
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
                color *= dist;
            }

            prev_point = point;
        }
    }


    // draw control points
    for (int i = 1; i < max_count - 1; ++i) {
        dist = SDFCircle(act_coord, control_points[i]);
        if (dist < EDGE + SMOOTH && SHOW_RED_DOTS)
        {
            dist = max(dist, 0.0);
            dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
            color *= mix(vec3(0.0,0.0, float(max_count - i) / float(max_count) ),vec3(1.0,1.0,1.0),dist);
        }
    }

    // draw start point
    dist = SDFCircle(act_coord, control_points[0]);
    if (dist < EDGE + SMOOTH && SHOW_RED_DOTS)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,1.0),dist);
    }

    // draw end point
    dist = SDFCircle(act_coord, control_points[max_count-1]);
    if (dist < EDGE + SMOOTH && SHOW_RED_DOTS)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,1.0),dist);
    }

    // draw lines between control points
    if (SHOW_GREY_LINES) {
        for (int i = 0; i < max_count - 1; ++i) {
            dist = DistanceToLineSegment(act_coord, control_points[i], control_points[i + 1]) * 2.0;
            if (dist < EDGE + SMOOTH && SHOW_GREY_LINES)
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
                color *= mix(vec3(0.8),vec3(1.0,1.0,1.0),dist);
            }
        }
    }

    // draw support lines and points
    float h = 1.0;
    float u = (1.0 - t);
    int n = max_count - 1;
    int n1 = n + 1;
    vec2 Q = control_points[0];
    vec2 prev_Q = Q;

    if (t != 0.0) {
        if (t <= 0.5) {
            u = t / u;
            for (int k = 1; k <= n; ++k) {
                float prev_h = h;
                h = h * u * (float(n1) - float(k));
                h = h / (float(k) + h);
                float h1 = (h / prev_h) * ((float(k) * (1.0 - t)) / (t * float(n - k + 1)));
                Q = h1 * Q + h * control_points[k];

                dist = DistanceToLineSegment(act_coord, prev_Q, control_points[k]) * 2.0;
                if (dist < EDGE + SMOOTH && show_support_lines[k-1])
                {
                    dist = max(dist, 0.0);
                    dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
                    color *= mix(support_lines_colors[k-1], vec3(1.0,1.0,1.0),dist);
                }

                dist = SDFCircle(act_coord, Q);
                if (dist < EDGE + SMOOTH && show_support_points[k-1])
                {
                    dist = max(dist, 0.0);
                    dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
                    color = mix(support_points_colors[k-1], vec3(1.0,1.0,1.0),dist);
                }

                prev_Q = Q;
            }
        }
        else {
            u = u / t;
            for (int k = 1; k <= n; ++k) {
                float prev_h = h;
                h = h * (float(n1) - float(k));
                h = h / (float(k) * u + h);
                float h1 = (h / prev_h) * ((float(k) * (1.0 - t)) / (t * float(n - k + 1)));
                Q = h1 * Q + h * control_points[k];

                dist = DistanceToLineSegment(act_coord, prev_Q, control_points[k]) * 2.0;
                if (dist < EDGE + SMOOTH && show_support_lines[k-1])
                {
                    dist = max(dist, 0.0);
                    dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
                    color *= mix(support_lines_colors[k-1], vec3(1.0,1.0,1.0),dist);
                }

                dist = SDFCircle(act_coord, Q);
                if (dist < EDGE + SMOOTH && show_support_points[k-1])
                {
                    dist = max(dist, 0.0);
                    dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
                    color = mix(support_points_colors[k-1], vec3(1.0,1.0,1.0),dist);
                }

                prev_Q = Q;
            }
        }     
    }

    // draw final point 
    h = 1.0;
    u = (1.0 - t);
    n = max_count - 1;
    n1 = n + 1;
    Q = control_points[0];

    if (t != 0.0) {
        if (t <= 0.5) {
            u = t / u;
            for (int k = 1; k <= n; ++k) {
                float prev_h = h;
                h = h * u * (float(n1) - float(k));
                h = h / (float(k) + h);
                float h1 = (h / prev_h) * ((float(k) * (1.0 - t)) / (t * float(n - k + 1)));
                Q = h1 * Q + h * control_points[k];
            }
        }
        else {
            u = u / t;
            for (int k = 1; k <= n; ++k) {
                float prev_h = h;
                h = h * (float(n1) - float(k));
                h = h / (float(k) * u + h);
                float h1 = (h / prev_h) * ((float(k) * (1.0 - t)) / (t * float(n - k + 1)));
                Q = h1 * Q + h * control_points[k];
            }
        }
    }

    vec2 point = Q;

    dist = SDFCircle(act_coord, point);
    if (dist < EDGE + SMOOTH && SHOW_FINAL_DOT)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color = mix(final_dot_color, vec3(1.0,1.0,1.0),dist);
    }


    fragColor = vec4(color,1.0);
}

