float hash12(vec2 p){
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

vec2 hash22(vec2 uv){
    float n = hash12(uv);
    return vec2(n, hash12(uv + n));
}

vec3 hash33(vec3 v){
    return vec3(hash12(v.xy), hash12(v.yz), hash12(v.xz));
}
//------------------
float mDist(vec2 v1,vec2 v2){
    return abs(v1.x - v2.x) + abs(v1.y - v2.y);
}

float Voronoi2D(vec2 uv){
    vec2 id = floor(uv);
    vec2 gv = uv-id;
    float d = 1.;

    for(int i=-1 ; i<=1 ; i++){
        for(int j=-1 ; j<=1 ; j++){
            vec2 off = vec2(i,j);
            vec2 v = hash22(id+off)+off;
            d = min(d,distance(gv,v));
        }
    }

    return 1.-d;
}

