}

const int MAX_MARCHING_STEPS = 500; // Max steps you are willing to search for a "hit" 
const int MAX_STORED_DATA = 100;
const int MAX_DATA_BLOCK = MAX_STORED_DATA / 2;
const float MAX_DIST = 1000.0; // Max distance to search for a "hit"
const float MIN_DIST = 0.0; // Where to start the march 
const float EPSILON = 0.001; // How close you need to be to be considered a hit

// Speaker in audio data
float data = 0.0;
float prev_data = 0.0;
float stored_data[MAX_STORED_DATA];
float data_output[MAX_STORED_DATA];

float prev_input = 0.0;
int data_index = 0;

struct Ray
{
    vec3 origin;
    vec3 direction;
};

struct Sphere
{
    vec3 center;
    float radius;
};

Sphere sphere = Sphere(vec3(0.0, 0.0, 0.0), 1.0);


float low_pass(float data_input)
{
    stored_data[data_index] = data_input;
    float data_output = 0.0;

    for (int i=0; i<MAX_DATA_BLOCK; ++i)
    {
        data_output += (1.0/float(MAX_STORED_DATA))*stored_data[i];
    }

    if (data_index == MAX_STORED_DATA)
    {
        data_index = 0;
    }

    return data_output;
}

