void rotate_2d(float freq, inout vec2 p)
{
    float c = cos(freq);
    float s = sin(freq);
    p = vec2((p.x*c) - (p.y*s), (p.x*s) + (p.y*c));
}

float distort_surface(float freq, vec3 point)
{
    return 8.0*data*sin(point.x*freq) * sin(point.y*freq) * sin(point.z*freq);
}

vec3 ray_direction(float fieldOfView, vec2 uv) 
{ 
    float z = 1.0 / tan(radians(fieldOfView) / 2.0); // Distance to image plane
    return normalize(vec3(uv, z));
}

float sphere_sdf(vec3 point)
{
    return length(point - sphere.center) - sphere.radius; // Return distance from sphere
}

float map_world(vec3 point)
{   
    rotate_2d(iTime/50.0, point.xy);

    float displacement = distort_surface(25.0*sin(iTime/100.0)+30.0, point);
    float dist = sphere_sdf(point); // Distance from sphere 

    return dist + displacement;
}

float shortest_distance_to_surface(vec3 eye_pos, vec3 march_dir, float start, float end)
{
    float depth = start; // Starting point for ray march (0.0)
    float dist = 0.0;    // Initalize distance from sphere variable
    int i = 0;           

    while (i < MAX_MARCHING_STEPS)
    {
        dist = map_world(eye_pos + (depth * march_dir)); // Distance to sphere (distorted)

        if (dist < EPSILON) // If at the surface, return the depth from image plane
        {
            return depth;
        }

        depth += dist;

        if (depth >= end) // If at the max marching distance, return the max value
        {
            return end;
        }

        i += 1;
     }

     return end;
}

vec3 estimate_normal(vec3 point)
{   
    vec3 step = vec3(EPSILON, 0.0, 0.0);

    float gradient_x = map_world(point + step) - map_world(point - step);
    float gradient_y = map_world(point + step.yxz) - map_world(point - step.yxz);
    float gradient_z = map_world(point + step.yzx) - map_world(point - step.yzx);

    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);

    return normalize(normal);
}

float diffuse(vec3 current_pos, vec3 light_pos, vec3 normal)
{
    vec3 light_dir = normalize(light_pos - current_pos);
    float intensity = max(0.0, dot(normal, light_dir));
    return intensity;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;
    float aspect_ratio = iResolution.x/iResolution.y;
    uv.x = uv.x * aspect_ratio; // Account for image aspect ratio

    data  = texture(iChannel0, vec2(uv.x, 0.75)).x; 
    float wave = (data-0.5)*2.0;
    data = 10.0*low_pass(data);

    sphere.radius = wave*sin(data)+1.0 *sphere.radius;
    sphere.center.x = 0.15*sin(iTime);
    sphere.center.y = 0.15*cos(iTime);


    // Define camera position
    vec3 cam_pos = vec3(0.0, 0.0, -2.0);

    // Define ray as vector from camera position to pixel position
    Ray ray = Ray(cam_pos, ray_direction(45.0, uv));

    // Define light position as a 3D vector
    vec3 light_pos = vec3(20.0, 50.0, -50.0);

    // Find distance to surface
    float dist = shortest_distance_to_surface(ray.origin, ray.direction, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) // If sphere doesn't exist at this pixel draw bg
    { 
        // Eventually want to have the background be a cubic room 
        fragColor = vec4(0.2, 0.2, 0.2, 0.0);
    }

    else // If sphere does exist at this pixel, draw color accordingly using basic lighting
    {
        vec3 point = ray.origin + dist*ray.direction;
        vec3 normal = estimate_normal(point);
        float intensity = diffuse(point, light_pos, normal);
        normal = vec3(0.85, 0.95, 0.95) * (intensity);
        normal = normal+0.15;
        if( ( (uv.y<=0.0) && (abs(uv.y-wave)<=0.05) ) || ( (uv.y>=0.0) && (abs(uv.y-wave)<=0.05) ) )
        {
            normal.x = 0.85;
            normal.y = 0.95;
            normal.z = 0.95;
        }
        fragColor = vec4(normal, 1.0);

    }
}

// uncomment to transform L2 to L1 space, rotate in L1 and 
// transform back from L1 to L2, to see how polar transformations
// in L1 are equivalent to their counterpart in L2.
//#define DEMO_POLAR_EQUIVALENCY

// uncomment to demonstrate rotation in L2
// uncommenting DEMO_POLAR_EQUIVALENCY will work in the inverse way
//#define DEMO_L2_NORM_ROTATION

const float L1tau = 8.0;
const float L1pi = 4.0;

// sign without zero
float nzsign(float x) {
    return (x < 0.0)?-1.0:1.0;
}

float L1cos(float x) {
    return abs(mod(x, 8.0) - 4.0) / 2.0 - 1.0;
}

float L1sin(float x) {
    return L1cos(x - 2.0);
}

float L1radians(float x) {
    return x / 45.0;
}

float L1degrees(float x) {
    return x * 45.0;
}

vec2 L1polar(vec2 p) {
    float r = abs(p.x) + abs(p.y);
    return (r == 0.0)?vec2(0.0):vec2(2.0 * nzsign(p.y) * (1.0 - p.x / r), r);
}

vec2 L1cartesian(vec2 p) {
    float a = p.x;
    float r = p.y;
    a = mod(a + 4.0, 8.0) - 4.0;
    float cos_x = 1.0 - abs(a) / 2.0;
    return r * vec2(cos_x, sign(a)*(1.0 - abs(cos_x)));
}

vec2 L1rotate (vec2 p, float q) {
    p = L1polar(p);
    return L1cartesian(vec2(p.x + q, p.y));
}

const float L2tau = 6.28318530718;
const float L2pi = 3.14159265359;

vec2 L2polar(vec2 p) {
    float r = length(p);
    return vec2(atan(p.y, p.x), r);
}

vec2 L2cartesian(vec2 p) {
    float a = p.x;
    float r = p.y;
    return r * vec2(cos(a), sin(a));
}

vec2 L2rotate (vec2 p, float q) {
#if 0 // logical
    p = L2polar(p);
    return L2cartesian(vec2(p.x + q, p.y));
#else // fast
    float c = cos(q);
    float s = sin(q);
    return vec2(c*p.x - s*p.y, c*p.y + s*p.x);
#endif
}

// convert polar coordinates from L1 to L2 
vec2 L1toL2(vec2 p) {
    return vec2(p.x * L2tau / L1tau, p.y);
}

// convert polar coordinates from L2 to L1
vec2 L2toL1(vec2 p) {
    return vec2(p.x * L1tau / L2tau, p.y);
}

vec3 hue(float hue) {
    return clamp( 
        abs(mod(hue * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 
        0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (fragCoord.xy / iResolution.xy)*2.0-1.0;	
	p.x *= iResolution.x/iResolution.y;

#ifndef DEMO_L2_NORM_ROTATION

#ifdef DEMO_POLAR_EQUIVALENCY
    p = L1cartesian(L2toL1(L2polar(p)));
#endif

    p = L1rotate(p, -iTime*0.5);

#ifdef DEMO_POLAR_EQUIVALENCY
    p = L2cartesian(L1toL2(L1polar(p)));
#endif

#else

#ifdef DEMO_POLAR_EQUIVALENCY
    p = L2cartesian(L1toL2(L1polar(p)));
#endif

    // the two norms have different arc lengths, so we need
    // to adjust speed for equivalency
    p = L2rotate(p, -iTime*0.5 * L2tau / L1tau);

#ifdef DEMO_POLAR_EQUIVALENCY
    p = L1cartesian(L2toL1(L2polar(p)));
#endif

#endif

    vec2 o = L1polar(p);
    vec3 color = texture(iChannel0, p).rrr * (hue(o.r/L1tau) * 0.5 + 0.5) * (0.7 + 0.3*clamp(L1cos(o.g * L1tau * 10.0)*10.0*0.5+0.5,0.0,1.0));

	fragColor = vec4(color, 1.0);
}

