struct Linear { float a; float b; };
struct Quadratic { float a; float b; float c; };
struct Quartic { float a; float b; float c; float d; float e; };

// Polynomial arithmetic makes things much easier and intuitive
Quadratic add(in Quadratic a, in float b) { a.c += b; return a; }
Quartic add(in Quartic a, in float b) { a.e += b; return a; }
Quadratic add(in Quadratic a, in Quadratic b) { return Quadratic(a.a + b.a, a.b + b.b, a.c + b.c); }
Quartic mul(in Quadratic a, in Quadratic b) { return Quartic(a.a * b.a, a.a * b.b + a.b * b.a, a.a * b.c + a.b * b.b + a.c * b.a, a.b * b.c + a.c * b.b, a.c * b.c); }
Quadratic square(in Linear a) { return Quadratic(a.a * a.a, 2.0 * a.a * a.b, a.b * a.b); }

// Automatic differentiation utilities (in three variables)
vec4 add(in vec4 f, in float g) { f.x += g; return f; }
vec4 mul(in vec4 f, in vec4 g) { return vec4(f.x * g.x, dot(f.xy, g.yx), dot(f.xz, g.zx), dot(f.xw, g.wx)); }
vec4 square(in vec4 f) { return vec4(f.x * f.x, 2.0 * f.x * f.yzw); }

// Smallest positive root of a quartic
float quartic2(in float a, in float b, in float c, in float d, in float e) {
    vec4 roots;
    float t = -1.0;
    for (int n=0; n < quartic(b / a, c / a, d / a, e / a, roots); n++) {
        if (roots[n] > 0.0) {
            t = t < 0.0 ? roots[n] : min(t, roots[n]);
        }
    }

    return t;
}

float iBlobbies(in vec3 ro, in vec3 rd, in float size1, in float size2, in vec3 pos1, in vec3 pos2, in float blobbiness) {
    vec3 ro1 = ro - pos1, ro2 = ro - pos2;
    Linear x1 = Linear(rd.x, ro1.x), y1 = Linear(rd.y, ro1.y), z1 = Linear(rd.z, ro1.z);
    Linear x2 = Linear(rd.x, ro2.x), y2 = Linear(rd.y, ro2.y), z2 = Linear(rd.z, ro2.z);

    Quadratic blob1 = add(add(square(x1), add(square(y1), square(z1))), -size1);
    Quadratic blob2 = add(add(square(x2), add(square(y2), square(z2))), -size2);

    Quartic blobbies = add(mul(blob1, blob2), -blobbiness);
    return quartic2(blobbies.a, blobbies.b, blobbies.c, blobbies.d, blobbies.e);
}

vec3 nBlobbies(in vec3 p, in float size1, in float size2, in vec3 pos1, in vec3 pos2, in float blobbiness) {
    vec3 p1 = p - pos1, p2 = p - pos2;
    vec4 x1 = vec4(p1.x, 1.0, 0.0, 0.0), y1 = vec4(p1.y, 0.0, 1.0, 0.0), z1 = vec4(p1.z, 0.0, 0.0, 1.0);
    vec4 x2 = vec4(p2.x, 1.0, 0.0, 0.0), y2 = vec4(p2.y, 0.0, 1.0, 0.0), z2 = vec4(p2.z, 0.0, 0.0, 1.0);

    vec4 blob1 = add(square(x1) + square(y1) + square(z1), -size1);
    vec4 blob2 = add(square(x2) + square(y2) + square(z2), -size2);

    vec4 blobbies = add(mul(blob1, blob2), -blobbiness);
    return normalize(blobbies.yzw);
}

vec4 render(in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    float time = 1406.42;

    vec2 mouse = iMouse.z > 0.0 ? (iMouse.xy - center) / iResolution.y * 3.14 : vec2(0.0, -0.5);
    vec2 uv = (fragCoord - center) / iResolution.y;

    vec3 ro = vec3(0.0, 0.0, 10.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    // Rotate with mouse
    float cy = cos(mouse.x), sy = sin(mouse.x);
    float cp = cos(mouse.y), sp = sin(mouse.y);

    ro.yz *= mat2(cp, -sp, sp, cp);
    ro.xz *= mat2(cy, -sy, sy, cy);
    rd.yz *= mat2(cp, -sp, sp, cp);
    rd.xz *= mat2(cy, -sy, sy, cy);

    vec4 fragColor = texture(iChannel0, rd);

    vec3 pos = vec3(cos(iTime) * 2.0, sin(iTime), cos(iTime) * sin(iTime)) * 3.0;
    float t = iBlobbies(ro, rd, 2.0, 5.0, pos, vec3(0.0), 5.0);
    if (t > 0.0) {
        vec3 hit = ro + rd * t;
        vec3 n = nBlobbies(hit, 2.0, 5.0, pos, vec3(0.0), 5.0);
        vec4 envMap = texture(iChannel0, reflect(rd, n));
        fragColor = envMap * abs(dot(rd, n));
    }

    return fragColor;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = render(fragCoord);
}

