		float hlhh = rand(vec4( floor(pos.x/float(o))+1.0, floor(pos.y/float(o)),     floor(pos.z/float(o))+1.0, floor(pos.w/float(o))+1.0 ));
		float lhhh = rand(vec4( floor(pos.x/float(o)),     floor(pos.y/float(o))+1.0, floor(pos.z/float(o))+1.0, floor(pos.w/float(o))+1.0 ));
		float hhhh = rand(vec4( floor(pos.x/float(o))+1.0, floor(pos.y/float(o))+1.0, floor(pos.z/float(o))+1.0, floor(pos.w/float(o))+1.0 ));

		float llzl = llll * (1.0 - lin.z) + llhl * lin.z;
		float hlzl = hlll * (1.0 - lin.z) + hlhl * lin.z;
        float lhzl = lhll * (1.0 - lin.z) + lhhl * lin.z;
		float hhzl = hhll * (1.0 - lin.z) + hhhl * lin.z;
		float llzh = lllh * (1.0 - lin.z) + llhh * lin.z;
		float hlzh = hllh * (1.0 - lin.z) + hlhh * lin.z;
		float lhzh = lhlh * (1.0 - lin.z) + lhhh * lin.z;
		float hhzh = hhlh * (1.0 - lin.z) + hhhh * lin.z;

        float lyzl = llzl * (1.0 - lin.y) + lhzl * lin.y;
		float hyzl = hlzl * (1.0 - lin.y) + hhzl * lin.y;
		float lyzh = llzh * (1.0 - lin.y) + lhzh * lin.y;
		float hyzh = hlzh * (1.0 - lin.y) + hhzh * lin.y;

        float xyzl = lyzl * (1.0 - lin.x) + hyzl * lin.x;
        float xyzh = lyzh * (1.0 - lin.x) + hyzh * lin.x;

        float val = xyzl * (1.0 - lin.w) + xyzh * lin.w;

        multiplier = multiplier + float(o);
		total = total + val * float(o);
	}
	return total/multiplier;
}

bool getNoise(vec4 pos, float dist) {
    return
        noise(pos*10.0 + vec4(1.5, 0.5, -3.2, 6.5), 12) > 0.56 ||
        noise(pos*8.0 + vec4(0.5, 6.5, -3.2, 1.5), 12) > 0.56;
}

vec3 hsv(float hue, float sat, float val) {
    hue = mod(hue, 1.0);
    hue *= 6.0;
    if (hue < 1.0) {
        return vec3(
            1.0,
            hue + (1.0-hue)*sat,
            sat
        )*val;
    } else if (hue < 2.0) {
        return vec3(
            2.0-hue + (hue-1.0)*sat,
            1.0,
            sat
        )*val;
    } else if (hue < 3.0) {
        return vec3(
            sat,
            1.0,
            hue-2.0 + (3.0-hue)*sat
        )*val;
    } else if (hue < 4.0) {
        return vec3(
            sat,
            4.0-hue + (hue-3.0)*sat,
            1.0
        )*val;
    } else if (hue < 5.0) {
        return vec3(
            hue-4.0 + (5.0-hue)*sat,
            sat,
            1.0
        )*val;
    } else {
        return vec3(
            1.0,
            sat,
            6.0-hue + (hue-5.0)*sat
        )*val;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float dist = 0.0;
    vec3 dir = vec3(uv*2.0-1.0, 1.0);
    vec2 r = iMouse.xy/vec2(640.0/PI/2.0, 360.0/PI) + vec2(0.0, PI/2.0);
    vec2 tmp = dir.yz;
    dir.y = tmp.x*cos(r.y) + tmp.y*sin(r.y);
    dir.z = tmp.y*cos(r.y) - tmp.x*sin(r.y);
    tmp = dir.xz;
    dir.x = tmp.x*cos(r.x) - tmp.y*sin(r.x);
    dir.z = tmp.y*cos(r.x) + tmp.x*sin(r.x);
    float len = length(dir);
    dir /= len;
    vec3 pos = vec3(1.0 + sin(iTime/10.0), 10.0 - iTime/10.0, -2.0 + cos(iTime/10.0));
    bool seen = false;
    for (int i = 0; i < 10; i++) {
        dist += float(i)/10.0;
        if (getNoise(vec4(pos + dist*dir, iTime/10.0), dist)) {
            seen = true;
            break;
        }
    }
    vec3 hit = vec3(0.0, 0.0, 0.0);
    vec3 col = vec3(0.0, 0.0, 0.0);
    if (seen) {
        for (; dist > 0.0; dist -= 0.1) {
            if (!getNoise(vec4(pos + dist*dir, iTime/10.0), dist)) {
                hit = pos + dist*dir;
                break;
            }
        }
        dir *= len;
        if (seen) {
            float val = 1.0-pow(dist/5.0, 3.0);
            col = hsv(hit.y/10.0, 0.8+val*0.1, val);
        }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

