float tCur, dstFar, nRun;
int idObj;
const float pi = 3.1415927;

#define BIG_SYS  1  // same in all shaders

#if BIG_SYS
const int nObj = 112;
#else
const int nObj = 56;
#endif

const ivec3 nSphObjD = ivec3 (6, 3, 2);
const int nSphObj = nSphObjD.x * nSphObjD.y * nSphObjD.z;
const int nSphTot = nObj * nSphObj;
const int nPre = 2;

vec3 GetR (int k)
{
  return Loadv4 (nPre + 4 * k).xyz;
}

vec4 GetQ (int k)
{
  return Loadv4 (nPre + 4 * k + 2);
}

vec4 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp, fId, u;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    u = (v + dn) * rd;
    fId = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    qBlk = vec2 (dot (u.zxy, fId), dot (u.yzx, fId));
  }
  return vec4 (dMin, fId);
}

float BlkHitSh (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) dMin = dn;
  return dMin;
}

float ObjHit (vec3 ro, vec3 rd)
{
  mat3 mRot;
  vec4 cdn4;
  vec3 rm, rom, rdm;
  vec2 qBlkN;
  float dMin;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nObj; n ++) {
    rm = GetR (n);
    mRot = QtToRMat (GetQ (n));
    rom = (ro - rm) * mRot;
    rdm = rd * mRot;
    cdn4 = BlkHit (rom, rdm, bSize);
    if (cdn4.x < dMin) {
      dMin = cdn4.x;
      fcBlk = cdn4.yzw;
      qBlkN = qBlk;
      idObj = n;
    }
  }
  qBlk = qBlkN;
  return dMin;
}

float ObjHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 mRot;
  vec3 rm, rom, rdm;
  float dMin;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nObj; n ++) {
    rm = GetR (n);
    mRot = QtToRMat (GetQ (n));
    rom = (ro - rm) * mRot;
    rdm = rd * mRot;
    dMin = min (dMin, BlkHitSh (rom, rdm, bSize));
  }
  return smoothstep (0.5 * rng, rng, dMin);
}

float Spots (vec2 q, int nSpot)
{
  float bSz, w;
  bSz = 0.6 * bSize.x;
  if (nSpot == 2 || nSpot == 3) q = Rot2D (q, 0.25 * pi);
  else if (nSpot == 6) q = q.yx;
  w = 1.;
  if (nSpot == 1) w = length (q);
  else if (nSpot == 2) w = length (abs (q) - vec2 (0.4 * bSz, 0.));
  else if (nSpot == 3) w = length (vec2 (mod (q.x + 0.35 * bSz, 0.7 * bSz) - 0.35 * bSz, q.y));
  else if (nSpot == 4) w = length (abs (q) - 0.4 * bSz);
  else if (nSpot == 5) w = min (length (abs (q) - 0.4 * bSz), length (q));
  else if (nSpot == 6) w = length (vec2 (mod (q.x + 0.26 * bSz, 0.52 * bSz) - 0.26 * bSz,
     abs (q.y) - 0.4 * bSz));
  return smoothstep (-0.02, 0., w - 0.2 * bSz);
}

vec3 ObjCol (vec3 rd, float sh)
{
  vec3 vn, col, q;
  float fId, sp, ep, em;
  int ns1, ns2, n28;
  col = vec3 (0.95, 0.95, 1.); 
  fId = float (idObj);
  ep = smoothstep (0., 0.2, PrBox2Df (qBlk, ((abs (fcBlk.x) == 1.) ? bSize.zy :
     ((abs (fcBlk.y) == 1.) ? bSize.xz : bSize.yx)) - 0.2));
  col *= 1. - 0.3 * ep;
  sp = 1.;
  em = 1.;
  if (mod (floor (fId / 8.), 2.) != 0. && fcBlk.z == 2. * mod (fId, 2.) - 1. ||
     mod (floor (fId / 8.), 2.) == 0. && fcBlk.z == 2. * mod (floor (fId / 2.), 2.) - 1.) {
    n28 = int (mod (fId, 28.));
    ns1 = int (0.5 * (-1. + sqrt (float (1 + 8 * n28))));
    ns2 = n28 - ns1 * (ns1 + 1) / 2;
    sp = Spots (vec2 (qBlk.x, qBlk.y - 0.5 * bSize.x * sign (qBlk.y)),
       ((qBlk.y > 0.) ? ns1 : ns2));
    col = mix (vec3 (0., 0., 1.), col, sp);
    em = smoothstep (0., 0.02, 0.1 - abs (qBlk.y));
    col *= 1. - 0.3 * em;
  }
  vn = fcBlk;
  if (sp < 1. || max (ep, em) > 0.) {
    q = vec3 (qBlk, 0.);
    if (abs (fcBlk.x) == 1.) q = q.zxy;
    else if (abs (fcBlk.y) == 1.) q = q.xzy;
    vn = VaryNf (32. * q, vn, 0.5);
  }
  vn = QtToRMat (GetQ (idObj)) * vn;
  col = col * (0.2 +  0.8 * sh * max (dot (vn, ltDir), 0.)) +
     0.2 * sh * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
  return col;
}

vec3 BgCol (vec3 ro, vec3 vn, float sh)
{
  vec3 col;
  col = mix (vec3 (0.7, 0.5, 0.5), vec3 (0.8, 0.85, 0.8),
     smoothstep (0., 0.07, abs (abs (Fbm2 (0.1 * ro.xz) - 0.4) - 0.2)));
  col *= 0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.);
  col *= 0.2 + 0.8 * smoothstep (0.85, 0.95, dot (normalize (200. * ltDir - ro), ltDir));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col;
  float dstObj, sh;
  bool isBg;
  bSize = 0.5 * blkGap * (vec3 (nSphObjD) - 1.) + 0.4;
  dstObj = ObjHit (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    isBg = false;
  } else {
    ro += ((0.5 - ro.y) / rd.y) * rd;
    isBg = true;
  }
  sh = 0.6 + 0.4 * ObjHitSh (ro + 0.01 * ltDir, ltDir, 60.);
  if (! isBg) col = ObjCol (rd, sh);
  else col = BgCol (ro, vec3 (0., 1., 0.), sh);
  if (isBg && rd.y < 0.) {
    rd = reflect (rd, vec3 (0., 1., 0.));
    ro += 0.01 * rd;
    dstObj = ObjHit (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      col = mix (col, ObjCol (rd, 1.), 0.3);
    }
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat, mPtr;
  vec3 col, rd, ro;
  vec2 canvas, uv;
  float az, el, asp, zmFac, sr, h;
  canvas = iResolution.xy;
