float box(vec3 p,vec3 b){
    vec3 q = abs(p)-b;
    return length(max(vec3(0.),q))+min(0.,max(q.x,max(q.y,q.z)));
}
float diam(vec3 p,float s){
    p = abs(p);
    return (p.x+p.y+p.z-s)*0.57735027;
}
//from Iq
float checkersTextureGradBox( in vec2 p, in vec2 ddx, in vec2 ddy )
{
    vec2 w = max(abs(ddx), abs(ddy)) + 0.01;  
    vec2 i = 2.0*(abs(fract((p-0.5*w)/2.0)-0.5)-abs(fract((p+0.5*w)/2.0)-0.5))/w;
    return 0.5 - 0.5*i.x*i.y;                  
}

mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
vec2 sdf(vec3 p){
   vec3 op = p;
   vec2 h;
   h.x = length(p)-1.;
   h.y = 1.;

  p.xz*=rot(.7885+iTime*.125);
  p.xz = abs(p.xz)-2.;
  p+=vec3(0.,.25,0.);
  p.xz *=rot(.785);
  p.xy *=rot(.785+iTime);
  vec2 t;
  t.x = mix(box(p,vec3(.7)),diam(p,.7),(sin(iTime*.66)*.5+.5)*1.2)*.7; // Very nice result mixing box and diam
  t.y = 2.;
  h = t.x < h.x ? t:h;

  t.x = dot(op+1.,vec3(0.,1.,0.));
  t.y = 3.;

  h = t.x < h.x ? t:h;
   return h;
}

