vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec2 map( in vec3 p )
{
    vec2 res = vec2( 1e10, 0.0 );
    //p = mod(p + vec3(3.0), vec3(6.)) - vec3(3.0);
    res = opU( res, vec2( fractal(p).w, 1.0) );

    return res;
}

vec3 normal(vec3 p) 
{
    const float dx = 0.00001;
	const vec3 k = vec3(1,-1,0);
	return  normalize(k.xyy*map(p + k.xyy*dx).x +
                      k.yyx*map(p + k.yyx*dx).x +
                      k.yxy*map(p + k.yxy*dx).x +
                      k.xxx*map(p + k.xxx*dx).x);
}

bool trace(inout vec4 ro, vec3 rd)
{
    for(int i = 0; i < MAX_STEPS; i++)
    {
        float de = abs(map(ro.xyz).x); 
        float md = max(CAM_ANGLE*ro.w,MIN_DIST);
        ro += vec4(rd, 1.0)*(de - 2.0*step(de, md)*md); 
        if(de < md) return true;
        if(ro.w > MAX_DIST) return false;
    }
    return true;
}

float pow2(float x)
{
    return x*x;
}

vec3 fresnel(vec3 V, vec3 H, vec3 F0)
{
    return F0 + (1. - F0)*pow(1.0 - max(dot(V,H), 0.0), 5.0);
}

float NDF_ggx(vec3 m, vec3 n, float alpha)
{
    float alpha2 = alpha*alpha; 
    return alpha2/(PI*pow2( pow2(max(dot(n,m), 0.)) * (alpha2 - 1.0) + 1.0 ));
}

float G_ggx(float NdotV, float alpha)
{
    float alpha2 = alpha*alpha;
    return 2.0*NdotV/(NdotV + sqrt( mix(NdotV*NdotV, 1.0, alpha2) ));
}

const float aperture_size = 0.34;
vec2 aperture()
{
    vec2 r = rand2();
    return vec2(sin(TWO_PI*r.x), cos(TWO_PI*r.x))*sqrt(r.y);
}

bool getRay(vec2 uv, out vec3 ro, out vec3 rd)
{
    mat3 cam = getCam(get(CamA));

    vec2 apert_cent = -0.8*uv;

    vec2 ap = aperture();

    if(!(distance(ap, apert_cent) < 1.0)) return false;

    float apd = length(ap);

    vec3 daperture = ap.x*cam[0] + ap.y*cam[1];

    ro = get(CamP).xyz + aperture_size*daperture;

    float focus =2.5 + 0.8*pow(apd,5.0);


    rd = normalize(focus*(cam*vec3(FOV*uv, 1.0)) - aperture_size*daperture);

    return true;
}

vec4 render(vec2 fragCoord)
{
    fragCoord += rand2();
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 cp, rd;
    if(!getRay(uv, cp, rd)) return vec4(0,0,0,1);

    vec4 ro = vec4(cp,0.0);
    vec3 fcol;
    if(trace(ro, rd))
    {
        const float roughness = 0.07;
        vec3 N = normal(ro.xyz);
        vec3 col = sin(vec3(1,2,3)*length(ro.xyz)*16.0-2.)*0.5 + 0.5;

        vec3 V = -rd;
        vec3 R = reflect(rd, N);
        vec3 L = light;
        vec3 H = normalize(V + L);

        vec3 kS = fresnel(V, N, vec3(0.7));
        vec3 kD = 1.0 - kS;

        float NdotL = max(dot(N, L), 2e-3);
        float NdotV = max(dot(N, V), 2e-3);

        float selfshadow = G_ggx(NdotL,roughness)*G_ggx(NdotV,roughness)/max(4.0*NdotL*NdotV,1e-3);
        vec3 specular = selfshadow*kS*NDF_ggx(H, N, roughness);  

        vec3 direct = (kD * col / PI + tanh(4.*col)*specular) * NdotL;
        fcol = 5.*direct + 0.1*col;

    }
    else fcol = vec3(0.0);

    return vec4(fcol, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    LoadLevel(Levels[3]);

    rng_initialize(fragCoord, iFrame);

    fragColor = vec4(0.0);   
    //prev 
    if(get(CamP).w == 0.0)
        fragColor = texture(iChannel0, fragCoord/iResolution.xy);

   for(int i = 0; i < SPP; i++)
       fragColor += render(fragCoord);
}

