vec2 var2dual(in float x) { return vec2(x, 1.0); }
vec2 fMul(in vec2 a, in vec2 b) { return vec2(a.x * b.x, a.x * b.y + a.y * b.x); }
vec2 fDiv(in vec2 a, in vec2 b) { return vec2(a.x / b.x, (b.x * a.y - a.x * b.y) / (b.x * b.x)); }
vec2 fPow(in vec2 a, in vec2 b) { float q = pow(a.x, b.x); return vec2(q, q * (b.x * a.y / a.x + b.y * log(a.x))); }
vec2 fSquare(in vec2 z) { return vec2(z.x * z.x, 2.0 * z.x * z.y); }
vec2 fExp(in vec2 z, in float base) { float q = pow(base, z.x); return vec2(q, q * z.y); }
vec2 fSin(in vec2 z) { return vec2(sin(z.x), cos(z.x) * z.y); }
vec2 fCos(in vec2 z) { return vec2(cos(z.x), -sin(z.x) * z.y); }
vec2 fMin(in vec2 a, in vec2 b) { return a.x < b.x ? a : b; }
vec2 fMax(in vec2 a, in vec2 b) { return a.x > b.x ? a : b; }
vec2 fClamp(in vec2 z, in vec2 edge0, in vec2 edge1) { return fMax(edge0, fMin(edge1, z)); }
vec2 fSmoothstep(in vec2 edge0, in vec2 edge1, in vec2 z) { z = fClamp(fDiv(z - edge0, edge1 - edge0), vec2(0.0), vec2(1.0, 0.0)); vec2 sq = fSquare(z); return 3.0 * sq - 2.0 * fMul(sq, z);}
vec2 fMix(in vec2 a, in vec2 b, in vec2 t) { return a + fMul(b - a, t); }

// p: sample point
// o: disk origin
// r: disk radius
// v: disk velocity
// dt: frame duration
float movingDisk(in vec2 p, in vec2 o, in float r, in vec2 v, in float dt) {
    vec2 p0 = p - o, p1 = v * dt;

    float a = dot(p1, p1);
    float b = dot(p0, p1);
    float c = dot(p0, p0) - r * r;

    float h = b * b - a * c;
    if (h > 0.0) {
        vec2 t = clamp((vec2(-1.0, 1.0) * sqrt(h) - b) / a, 0.0, 1.0);
        return t.y - t.x;
    }

    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    vec2 diskX = 0.5 * fCos(6.0 * var2dual(iTime));
    vec2 diskY = 0.5 * fMul(fCos(6.0 * var2dual(iTime)), fSin(6.0 * var2dual(iTime)));
    color += movingDisk(uv, vec2(diskX.x, diskY.x), 0.1, vec2(diskX.y, diskY.y), iTimeDelta);

    fragColor = vec4(color, 1.0);
}

const vec3 BG    = vec3(.6);
const vec3 PEEL  = vec3(.0, .4, .0);
const vec3 PITH  = vec3(.95, 1, .9);
const vec3 FLESH = vec3(.7, .8, .0);
const int SECTIONS = 9;

const float PHI = 1.618033988749895; // (1+sqrt(5)) / 2
const float PI2 = 6.283185307179586; // 2*pi

// gold noise (shadertoy.com/view/ltB3zD)
float rand21(vec2 p, float seed) { return fract(tan(distance(p*PHI, p)*seed)*p.x); }
float minc(vec2 p) { return min(p.x, p.y); }
float noise(vec2 p, float seed) {
    vec2 r = floor(p);
    vec2 s = fract(p);
    float h00 = rand21(r,             seed);
    float h01 = rand21(r + vec2(0,1), seed);
    float h10 = rand21(r + vec2(1,0), seed);
    float h11 = rand21(r + vec2(1,1), seed);
    return mix(mix(h00, h01, s.y),
               mix(h10, h11, s.y),
               s.x);
}

float height(float r, float angle) {
    angle -= PI2 * float(SECTIONS) * .5; // section dividers are in the middle
    float scaledAngle = fract(angle / PI2) * float(SECTIONS);
    float section = floor(scaledAngle);
    float subAngle = fract(scaledAngle);
    return noise(vec2(r*8., subAngle * 15.), section + 100.);
}

// return the color of a lime with unit radius centered at the origin
vec3 lime(vec2 p) {
    float r = length(p);
    float angle = atan(p.y, p.x) + iTime*.2;

    float subpos, idx;
    {
        float scaledAngle = fract(angle / PI2) * float(SECTIONS);
        idx = floor(scaledAngle);
        float hsh1 = rand21(vec2(idx, 10.), 1.);
        float hsh2 = rand21(vec2(idx, 10.), 2.);
        subpos = fract(scaledAngle) + 0.03*sin((10.+2.*hsh1)*(r+hsh2));
    }

    float thresh = .94 + .005 * sin(angle*20.) + .0015 * cos(angle*65.+4.);
    float pthresh = thresh - .1*smoothstep(.2, -0.2, distance(subpos, .5));

    float flesh;
    {
        float width = atan(.015 / r);
        flesh = smoothstep(0., width, distance(subpos, .5));
        flesh *= smoothstep(pthresh-.02, pthresh-.04, r);
        float center = .04 + .02*smoothstep(.4, -0.2, distance(subpos, .5));
        flesh *= smoothstep(center, center + .02, r);
    }

    vec3 color;
    {
        float h = height(r, angle);
        color = mix(FLESH*.85, FLESH*1.1, h);
        vec2 p2 = p + vec2(-.02,.05);
        float r2 = length(p2);
        float angle2 = atan(p2.y, p2.x) + iTime*.2;
        float h2 = height(r2, angle2);
        color += .4 * smoothstep(.2, .6, h - h2);
    }
    color = mix(PITH, color, flesh);
    color = mix(color, PEEL, smoothstep(thresh, thresh+.05, r));
    color = mix(color, BG*.7, smoothstep(1., 1.015, r));
    color = mix(color, BG, smoothstep(1., 1.3, r));
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 res = iResolution.xy / 2.;
    float scale = minc(res) * .8;
    vec3 col = lime((fragCoord - res) / scale);
    fragColor = vec4(col,1.0);
}

