// very cheap AA. Uses 5 frames, but doesn't cost a thing. But: flickering.
// this also causes motion blur, by accident.
//  0   - no AA
//  1-5 -  valid AA levels
// everything below 0 or above 5 is ignored.
#define AA 5

// scene speed. "game time"
#define S (1.)

// def this if refraction doesn't work or looks weird.
//#define refract _refract

const float maxdist = 10000.;
const float nearcull = 0.;           // cull everything near the camera
const float shadowdist = 0.001;      // shadow ray distance (keep very small)
const float refdist = 0.001;         // reflection ray distance (keep very small)
const int refsteps = 4;              // reflection steps

vec3 dirlight = normalize(vec3(.8, -.8, 1.));
vec3 dirlightcolor = vec3(1., .8, .8);
vec3 fog = vec3(.8, .8, 1.);

vec3 _refract(vec3 I, vec3 N, float eta)
{
    eta += 2.;
    float e = eta*eta;
    float d = dot(N, I);
    float k = 1.0 - e*(1.0-d*d);
    if (k < 0.0)
        return vec3(0.);
    return eta*I - (eta*d + sqrt(k)) * N;
}

void movedirlight()
{
    dirlight = normalize(vec3(.8, -.8+cos(iTime*S), cos(iTime*S)*.5+.9));
}

struct Tp {
vec3 wp;
float dist;
vec3 norm;
int mat;
};

Tp nil(vec3 ro, vec3 rd, float d)
{
    return Tp(ro+d*rd, d, vec3(0.), 0);
}

Tp rtSphere(vec3 ro, vec3 rd, vec3 p, float r, int mat)
{
    float dotp = dot(rd, ro)-dot(rd, p);
    float dist = distance(ro, p);
    float dd = dotp*dotp - (dist*dist - r*r);
    if (dd < 0.)
        return nil(ro, rd, maxdist);
    float d1 = -dotp + sqrt(dd);
    float d2 = -dotp - sqrt(dd);
    float d = min(d1, d2);
    if (d < nearcull)
       return nil(ro, rd, maxdist);
    vec3 wp = ro+rd*d;
    vec3 n = normalize(wp-p);
    return Tp(wp, d, n, mat);
}

Tp rtPlane(vec3 ro, vec3 rd, vec3 pp, vec3 pn, int mat)
{
    float x = dot(rd, pn);
    if (x == 0.)
        return nil(ro, rd, maxdist);
    float d = dot(pp-ro, pn)/x;
    if (d < 0.)
        return nil(ro, rd, maxdist);
    return Tp(ro+d*rd, d, pn, mat);
}

float sdSphere(vec3 p, vec3 pos, float r)
{
    return distance(pos, p) - r;
}

float sdPlane(vec3 p, vec3 n, float h)
{
    return dot(p, n) + h;
}

Tp opUnion(Tp a, Tp b)
{
    float d = min(a.dist, b.dist);
    if (d == a.dist) return a;
    return b;
}

// the following two functions are the same for different techniques.
//
// distance function for the primitives. Used for AO
float mmap(vec3 ro)
{
    float plane1 = sdPlane(ro, vec3(0., 0., 1.), 0.);
    float plane2 = sdPlane(ro, normalize(vec3(-.5, 0., 1.)), 2.);
    float planes = min(plane1, plane2);
    float ball1 = sdSphere(ro, vec3(0.7, 15., 1.), 1.);
    float ball2 = sdSphere(ro, vec3(0.2, 15., .8)+vec3(sin(iTime*2.*S)*3., cos(iTime*2.*S)*2.4, 0.), .8);
    float balls = min(ball1, ball2);
    float rball = sdSphere(ro, vec3(1.2, 7., 1.), .3);
    return min(min(planes, balls), rball);
}

// raytrace the individual objects, combine them
Tp map(vec3 ro, vec3 rd)
{
    Tp plane1 = rtPlane(ro, rd, vec3(0.), vec3(0., 0., 1.), 1);
    Tp plane2 = rtPlane(ro, rd, vec3(0., 0., -2.), normalize(vec3(-.5, 0., 1.)), 2);
    Tp planes = opUnion(plane1, plane2);
    Tp ball1 = rtSphere(ro, rd, vec3(0.7, 15., 1.), 1., 3);
    Tp ball2 = rtSphere(ro, rd, vec3(0.2, 15., .8)+vec3(sin(iTime*2.*S)*3., cos(iTime*2.*S)*2.4, 0.), .8, 3);
    Tp balls = opUnion(ball1, ball2);
    Tp rball = rtSphere(ro, rd, vec3(1.2, 7., 1.), .3, 4);
    return opUnion(opUnion(planes, balls), rball);
}

// distance field AO, aka DFAO
float calcAO(Tp s)
{
    float d = 0.4;
    vec3 tp = s.wp + s.norm*d;
    return clamp(mix(.5, 1., mmap(tp)/d), 0., 1.);
}

vec3 color(Tp s)
{

    vec2 g = floor(mod(s.wp.xy, 2.));
    float m = clamp(mod(g.x+g.y, 2.), 0., 1.);
    if (s.dist >= maxdist)
        return fog;
    switch (s.mat){
    case 1: // white ground plane
        return mix(mix(vec3(1.), vec3(0.), m), vec3(.5), clamp(s.dist*.015, 0., 1.));
    case 2: // green angled plane
        return mix(mix(vec3(0., 1., 0.), vec3(0.), m), vec3(0., .5, 0.), clamp(s.dist*.015, 0., 1.));
    case 3: // red balls
        return vec3(.8, .02, 0.);
    case 4: // glass ball
        return vec3(0.1, 0.3, 0.2);
    }
    return vec3(clamp(mod(g.x+g.y, 2.), 0., 1.));
}

float getReflectivity(float d, Tp s)
{
    if (s.dist >= maxdist)
        return 1.;
    float refl = 1.;
    switch (s.mat){
    case 1:
        refl = mix(0.4, .97, pow(d, .6));
        break;
    case 2:
        refl = mix(0.3, .97, pow(d, .2));
        break;
    case 3:
        refl = mix(0.3, .57, pow(d, .8));
        break;
    case 4:
        refl = mix(0.4, .97, pow(d, .4));
        break;
    }
    return clamp(refl, 0., 1.);
}

// blend fog and stuff
vec3 pp(vec3 c, Tp s, vec2 uv)
{
    float h = clamp(1.1-s.wp.z*.05, 0., 1.);
    float d = clamp(s.dist*.002-.05, 0., 1.);
    return pow(mix(c, fog, d), vec3(1./2.2));
}

float calcShadow(Tp scene)
{
    Tp smap = map(scene.wp+shadowdist*scene.norm, dirlight);
    float ao = calcAO(scene);
    return clamp(pow(smap.dist, 6.), 0., 1.)*ao;
}

vec3 getLight(Tp s)
{
    return dirlightcolor * (clamp(dot(s.norm, dirlight), 0., 1.) * calcShadow(s)) + fog*.1;
}

vec3 getReflection(Tp s, vec3 rd)
{
    float d = dot(s.norm, rd);
    d = clamp(d*d, 0., 1.);
    vec3 ray = rd;
    Tp refs = s;
    vec3 refc = vec3(0.);
    for (int i=0; i<refsteps; i++){
        float d = dot(refs.norm, ray);
        d = d*d;
        ray = reflect(ray, refs.norm);
        vec3 rcol = color(refs);
        float r = getReflectivity(d, refs);
        refs = map(refs.wp+ray*refdist, ray);
        // which one of those next two lines is more realistic?
     //   refc += color(refs)*getLight(refs)*rcol*r;
        refc = mix(color(refs)*getLight(refs)*rcol, refc, r);
    }
    return refc;
}

// is the material refracting?
bool refracting(Tp s)
{
    return s.mat == 4;
}

vec4 getRefraction(Tp s, vec3 rd)
{
    float r1 = 1.05;
    float r2 = 1.0;
    if (!refracting(s))
        return vec4(0.);
    vec3 ray = refract(rd, s.norm, r1/r2);
    Tp scene = map(s.wp + refdist*ray, ray);
    if (!refracting(scene))
        return vec4(color(scene)*getLight(scene)*normalize(color(s)), clamp(scene.dist, 0., 1.));
    ray = refract(ray, scene.norm, r2/r1);
    Tp scout = map(scene.wp + refdist*ray, ray);
    return vec4(color(scout)*getLight(scout)*normalize(color(s)), clamp(scout.dist, 0., 1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ouv = uv;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 diff = ((fragCoord+vec2(1.))-fragCoord)/2./iResolution.xy;
    diff *= .5;

    movedirlight();

    float cammove = smoothstep(-.1, .8, cos(iTime*.8*S))*2.-1.;
    vec3 ro = vec3(sin(iTime*6.*S)*.02+cammove, cammove*5.+sin(iTime*9.*S)*.01, 2.-cammove+cos(iTime*5.*S)*.01);
    vec3 rd = normalize(vec3(uv.x, 2., uv.y));

#if AA != 0
    //   1 4
    //    0
    //   3 2
    switch (iFrame% max(5, min(0, AA)) ){
    case 0:
        break;
    case 1:
        rd = normalize(vec3(uv.x-diff.x, 2., uv.y-diff.y));
        break;
    case 2:
        rd = normalize(vec3(uv.x+diff.x, 2., uv.y+diff.y));
        break;
    case 3:
        rd = normalize(vec3(uv.x-diff.x, 2., uv.y+diff.y));
        break;
    case 4:
        rd = normalize(vec3(uv.x+diff.x, 2., uv.y-diff.y));
        break;
    }
#endif

    Tp scene = map(ro, rd);
    vec3 col = color(scene);
    vec3 lightcol = getLight(scene);
    vec3 refl = getReflection(scene, rd);
    vec4 refr = getRefraction(scene, rd);

    float d = dot(scene.norm, rd);
    d = d*d;

    col = mix(col, refr.rgb, refr.a);
    col = mix(refl, col*lightcol*lightcol, getReflectivity(d, scene));
    col = pp(col, scene, uv);
#if AA != 0
    if (iFrame != 0) {
        vec3 ocol = texture(iChannel0, ouv).rgb;
        col += ocol;
        col /= 2.;
    }
#endif

    fragColor = vec4(col, 1.);
}

