#define USE_DISTANCE_FIELD_NORMAL
#define CELL_TRAVERSALS 40
#define VOXEL_SIZE 0.25

float implicit(in vec3 p) {
    p *= VOXEL_SIZE; // Undo scaling in the implicit surface
    float n = mix(0.5, 6.0, 0.5 + 0.5 * sin(iTime));
    return pow(abs(p.x), n) + pow(abs(p.y), n) + pow(abs(p.z), n) - pow(1.5, n);
}

vec3 getNormal(in vec3 p) {
    vec2 e = vec2(0.001, 0.0);
    float m = implicit(p);
    return normalize(vec3(implicit(p + e.xyy) - m,
                          implicit(p + e.yxy) - m,
                          implicit(p + e.yyx) - m));
}

// Cubic solver
int solveCubic(in float a, in float b, in float c, in float d, out vec3 roots) {
    float u = b / (3.0 * a);

    float p = (c - b * u) / a;
    float q = (d - (c - 2.0 * b * b / (9.0 * a)) * u) / a;
    if (abs(p) < 1e-9) { roots.x = -sign(q) * pow(abs(q), 1.0 / 3.0) - u; return 1; }

    if (4.0 * p * p * p + 27.0 * q * q > 0.0) {
        float ip = 3.0 / abs(p), sp = sqrt(ip), k = 0.5 * q * ip * sp;
        if (p > 0.0) roots.x = -2.0 / sp * sinh(asinh(k) / 3.0) - u;
        else roots.x = -2.0 / sp * cosh(acosh(abs(k)) / 3.0) * sign(q) - u;
        return 1;
    }

    float m = sqrt(-p / 3.0);
    roots.x = -2.0 * m * sin(asin(1.5 * q / (p * m)) / 3.0);

    float h = sqrt(-3.0 * roots.x * roots.x - 4.0 * p);
    roots.yz = 0.5 * vec2(h - roots.x, -h - roots.x);
    roots -= u;

    return 3;
}

// Intersection
vec4 iTrilinearIsoSurf(in vec3 ro, in vec3 rd, in float a, in float b, in float c, in float d, in float e, in float f, in float g, in float h) {
    vec4 u = vec4(-a + b + c - d + e - f - g + h, a - b - c + d, a - b - e + f, a - c - e + g);
    vec3 v = vec3(b, c, e) - a;

    vec3 xxyyzz = ro.xxy * ro.yzz;
    vec3 uuvvww = rd.xxy * rd.yzz;

    float t3 = u.x * rd.x * rd.y * rd.z;
    float t2 = dot(ro.zyx, uuvvww) * u.x + dot(u.yzw, uuvvww);
    float t1 = dot(u, vec4(dot(xxyyzz, rd.zyx), dot(ro.xy, rd.yx), dot(ro.xz, rd.zx), dot(ro.yz, rd.zy))) + dot(v, rd);
    float t0 = u.x * ro.x * ro.y * ro.z + dot(u.yzw, xxyyzz) + dot(v, ro) + a;

    t3 += 0.000001; // To deal with precision issues that seem to arise with this surface

    vec4 hits;
    hits.w = float(solveCubic(t3, t2, t1, t0, hits.xyz));

    return hits;
}

// Normal
vec3 nTrilinearIsoSurf(in vec3 p, in float a, in float b, in float c, in float d, in float e, in float f, in float g, in float h) {
    vec4 u = vec4(-a + b + c - d + e - f - g + h, a - b - c + d, a - b - e + f, a - c - e + g);
    return normalize(u.x * p.yxx * p.zzy + u.yyz * p.yxx + u.zww * p.zzy + vec3(b, c, e) - a);
}

vec4 gridCell(in vec3 ro, in vec3 rd, in vec3 pos) {
    ro -= pos;

    // Isovalues
    float a = implicit(pos);
    float b = implicit(pos + vec3(1.0, 0.0, 0.0));
    float c = implicit(pos + vec3(0.0, 1.0, 0.0));
    float d = implicit(pos + vec3(1.0, 1.0, 0.0));
    float e = implicit(pos + vec3(0.0, 0.0, 1.0));
    float f = implicit(pos + vec3(1.0, 0.0, 1.0));
    float g = implicit(pos + vec3(0.0, 1.0, 1.0));
    float h = implicit(pos + 1.0);

    float sa = sign(a);
    if (sa == sign(b) && sa == sign(c) && sa == sign(d) &&
        sa == sign(e) && sa == sign(f) && sa == sign(g) &&
        sa == sign(h)) return vec4(-1.0, vec3(0.0));

    // Intersect
    vec4 hit = iTrilinearIsoSurf(ro, rd, a, b, c, d, e, f, g, h);
    float t = -1.0;
    vec3 hitPos;

    // Find closest
    for (int n=0; n < int(hit[3]); n++) {
        vec3 hitCandid = ro + rd * hit[n];
        if (hit[n] > 0.0 && (t < 0.0 ? true : hit[n] < t) && all(lessThan(abs(hitCandid - 0.5), vec3(0.5)))) {
            t = hit[n];
            hitPos = hitCandid;
        }
    }

    #ifdef USE_DISTANCE_FIELD_NORMAL
    vec3 nor = getNormal(pos + hitPos);
    #else
    vec3 nor = nTrilinearIsoSurf(hitPos, a, b, c, d, e, f, g, h);
    #endif

    return vec4(t, nor);
}

// Adapted from https://www.shadertoy.com/view/MdBGRm
// vec4(dist, normal)
vec4 traverse(vec3 ro, vec3 rd) {
    vec3 grid = floor(ro);
    vec3 gridStep = sign(rd);
    vec3 corner = max(gridStep, 0.0);

    vec3 ratio = (grid + corner - ro) / rd;
    vec3 ratioStep = gridStep / rd;

    for (int i=0; i < CELL_TRAVERSALS; i++) {
        vec4 hit = gridCell(ro, rd, grid);
        if (hit.x > 0.0) return hit;

        vec3 cp = step(ratio, ratio.yzx);
        vec3 mask = cp * (1.0 - cp.zxy);

        grid += gridStep * mask;
        ratio += ratioStep * mask;
	}

    return vec4(-1.0, 0.0, 0.0, 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    float time = iTime;

    vec2 mouse = ivec2(iMouse) == ivec2(0) ? vec2(0.0) : (iMouse.xy - center) / iResolution.y * 3.14;
    vec2 uv = (fragCoord - center) / iResolution.y;

    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    // Rotate with mouse
    float cy = cos(mouse.x), sy = sin(mouse.x);
    float cp = cos(mouse.y), sp = sin(mouse.y);

    ro.yz *= mat2(cp, -sp, sp, cp);
    ro.xz *= mat2(cy, -sy, sy, cy);
    rd.yz *= mat2(cp, -sp, sp, cp);
    rd.xz *= mat2(cy, -sy, sy, cy);

    // Environment map
    fragColor = texture(iChannel0, rd);

    // Intersect
    ro /= VOXEL_SIZE; // Shrink voxels to half size
    vec4 hit = traverse(ro, rd);
    vec3 hitPos = ro + rd * hit.x;

    // Render the shape if hitting
    if (hit.x > 0.0) {
        float diff = abs(dot(hit.yzw, -rd));
        fragColor = mix(vec4(hit.yzw, 1.0), texture(iChannel0, reflect(-rd, hit.yzw)), 0.25);
        fragColor.rgb *= diff;
    }

    fragColor.rgb = pow(fragColor.rgb, vec3(0.75));
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash21(in vec2 p) {
	vec3 p3 = fract(p.xyx * vec3(0.1031, 0.103, 0.0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy).x;
}

vec2 sdTruchet(in vec2 p) {
    vec2 cId = floor(p);
    vec2 cUv = fract(p) - 0.5;

    cUv.x *= sign(Hash21(cId) - 0.5);
    cUv -= cUv.x < -cUv.y ? -0.5 : 0.5;

    float t = atan(cUv.y, cUv.x);
    t *= sign(mod(cId.x + cId.y, 2.0) - 0.5);

    return vec2(abs(length(cUv) - 0.5), t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0 + 0.25 * iTime;

    vec2 truchet = sdTruchet(uv);
    float d = truchet.x - 0.1;

    // Nice colors from iq
    vec3 color = 1.0 - sign(d) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(d));
	color *= 0.8 + 0.2 * cos(140.0 * d);
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d) * 0.6));

    if (d < 0.02) {
        truchet.y -= 0.5 * iTime;
        float fade = 0.5 + 0.5 * sin(iTime);
        float vertStripes = abs(mod(d + 0.0333, 0.0666) - 0.0333);
        float horiStripes = abs(mod(truchet.y + 0.1, 0.2) - 0.1) * 0.5;
        color += fade * smoothstep(0.03, 0.0, min(vertStripes, horiStripes));
    }

    fragColor = vec4(color, 1.0);
}

