/* 
 Hi. I made this shader to make it easier to visualize Bezier curves and de Casteljau's algorithm.
 You can find more shaders to learn about Bezier curves
 on my playlist: https://www.shadertoy.com/playlist/X3XfWf (still WiP)
 Enjoy <3
*/

// changeable parameters (feel free to experiment!) 

#define BEZIER_CURVE_POINTS 5 // int, minimum = 2, maximum = 13

// Remember to change BEZIER_CURVE_POINTS parameter in "Buffer A" too! (second tab, you can select it ot the top of this window)
// After changing BEZIER_CURVE_POINTS peremeter you need to reset shader's time (button on the bottom of shader window)
// PS: higher values can be imposible to compile if your GPU is weak. Sorry

#define BACKWARDS_ANIMATION true // bool

#define SLOW_DOWN 2.0 // float, need to be typed with decimal point (.)

// sorry for naming the parameter "SLOW_DOWN" instead of "SPEED", but default speed is really fast
// and it's easier for you to slow down things by an integer instead of speeding up by for example 0.25.
// If you want to speed up the animation you can use a float lower than 1.0

// Information on the parameters below is provided in the comment

#define SHOW_RED_DOTS true // bool
#define SHOW_BLUE_DOT true // bool
#define SHOW_FINAL_DOT true // bool

#define SHOW_BLACK_LINE true // bool
#define SHOW_GREY_LINES true // bool

#define ALWAYS_SHOW_BEZIER_CURVE false // bool

#define CASTELJAUS_STEPS 50 // int, more than 1000 can really slow down your computer, be careful

#define SHOW_1_SUPPORT_POINT true // bool
#define SHOW_2_SUPPORT_POINT true // bool
#define SHOW_3_SUPPORT_POINT true // bool
#define SHOW_4_SUPPORT_POINT true // bool
#define SHOW_5_SUPPORT_POINT true // bool
#define SHOW_6_SUPPORT_POINT true // bool
#define SHOW_7_SUPPORT_POINT true // bool
#define SHOW_8_SUPPORT_POINT true // bool
#define SHOW_9_SUPPORT_POINT true // bool
#define SHOW_10_SUPPORT_POINT true // bool
#define SHOW_11_SUPPORT_POINT true // bool

#define SHOW_1_SUPPORT_LINE true // bool
#define SHOW_2_SUPPORT_LINE true // bool
#define SHOW_3_SUPPORT_LINE true // bool
#define SHOW_4_SUPPORT_LINE true // bool
#define SHOW_5_SUPPORT_LINE true // bool
#define SHOW_6_SUPPORT_LINE true // bool
#define SHOW_7_SUPPORT_LINE true // bool
#define SHOW_8_SUPPORT_LINE true // bool
#define SHOW_9_SUPPORT_LINE true // bool
#define SHOW_10_SUPPORT_LINE true // bool
#define SHOW_11_SUPPORT_LINE true // bool

// non-changeable parameters (don't get me wrong, feel free to experiment with the entire code,
// but you can screw something up :D )

#define EDGE   0.005   // anti-aliasing
#define SMOOTH 0.0025  // anti-aliasing 

#define BufA(x) texture(iChannel0, (vec2(x, 0.0) + 0.5) / iResolution.xy)


const int max_count = BEZIER_CURVE_POINTS;
const float radius = 20.0;
const float statePos = float(max_count);

const int factorials[13] = int[](
                           1,
                           1,
                           2,
                           6,
                           24,
                           120,
                           720,
                           5040,
                           40320,
                           362880,
                           3628800,
                           39916800,
                           479001600);

const vec3 support_lines_colors[11] = vec3[](
                                      vec3(1.0, 0.839, 0.458),
                                      vec3(1.0, 0.674, 0.478),
                                      vec3(1.0, 0.658, 0.560),
                                      vec3(1.0, 0.541, 0.788),
                                      vec3(0.992, 0.380, 1.0),
                                      vec3(0.788, 0.458, 1.0),
                                      vec3(0.635, 0.541, 1.0),
                                      vec3(0.380, 0.788, 1.0),
                                      vec3(0.321, 1.0, 0.803),
                                      vec3(0.807, 0.705, 0.592),
                                      vec3(0.509, 0.482, 0.678));

const vec3 support_points_colors[11]= vec3[](
                                      vec3(0.992, 0.792, 0.325),
                                      vec3(1.0, 0.639, 0.341),
                                      vec3(1.0, 0.454, 0.301),
                                      vec3(1.0, 0.039, 0.560),
                                      vec3(0.807, 0.0, 0.819),
                                      vec3(0.560, 0.0, 0.921),
                                      vec3(0.333, 0.160, 1.0),
                                      vec3(0.019, 0.662, 1.0),
                                      vec3(0.0, 0.980, 0.694),
                                      vec3(0.549, 0.443, 0.329),
                                      vec3(0.270, 0.250, 0.392));                                       

const vec3 final_dot_color = vec3(0.0,1.0,0.0);

const bool show_support_points[11] = bool[](
                                     SHOW_1_SUPPORT_POINT,
                                     SHOW_2_SUPPORT_POINT,
                                     SHOW_3_SUPPORT_POINT,
                                     SHOW_4_SUPPORT_POINT,
                                     SHOW_5_SUPPORT_POINT,
                                     SHOW_6_SUPPORT_POINT,
                                     SHOW_7_SUPPORT_POINT,
                                     SHOW_8_SUPPORT_POINT,
                                     SHOW_9_SUPPORT_POINT,
                                     SHOW_10_SUPPORT_POINT,
                                     SHOW_11_SUPPORT_POINT);

const bool show_support_lines[11] = bool[](
                                    SHOW_1_SUPPORT_LINE,
                                    SHOW_2_SUPPORT_LINE,
                                    SHOW_3_SUPPORT_LINE,
                                    SHOW_4_SUPPORT_LINE,
                                    SHOW_5_SUPPORT_LINE,
                                    SHOW_6_SUPPORT_LINE,
                                    SHOW_7_SUPPORT_LINE,
                                    SHOW_8_SUPPORT_LINE,
                                    SHOW_9_SUPPORT_LINE,
                                    SHOW_10_SUPPORT_LINE,
                                    SHOW_11_SUPPORT_LINE);

int BinomialCoefficient(int n, int k) {
    return factorials[n] / (factorials[k] * factorials[n-k]);
}

float DistanceToLineSegment(vec2 point, vec2 start, vec2 end) {
    float A = point.x - start.x;
    float B = point.y - start.y;
    float C = end.x - start.x;
    float D = end.y - start.y;

    float dot1 = A * C + B * D;
    float len_sq = C * C + D * D;
    float param = -1.0;
    if (len_sq != 0.0) {  param = dot1 / len_sq; }

    float xx, yy;

    if (param < 0.0) {
        xx = start.x;
        yy = start.y;
    }

    else if (param > 1.0) {
        xx = end.x;
        yy = end.y;
    }

    else {
        xx = start.x + param * C;
        yy = start.y + param * D;
    }

    float dx = point.x - xx;
    float dy = point.y - yy;
    return sqrt(dx * dx + dy * dy);
}

float SDFCircle( in vec2 coords, in vec2 offset )
{
    coords -= offset;
    float v = coords.x * coords.x + coords.y * coords.y - EDGE*EDGE;
    vec2  g = vec2(2.0 * coords.x, 2.0 * coords.y);
    return v / length(g); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 act_coord = ((fragCoord.xy / iResolution.xy) - vec2(0.25,0.5));
    float aspectRatio = iResolution.x / iResolution.y;
    act_coord.x *= aspectRatio;

    float dist;

    vec2 control_points[max_count];

    vec4 state = BufA(statePos);
    vec3 color = vec3(1.0,1.0,1.0);

    for (int i = 0; i < max_count; ++i) {
        float f = float(i);
        if (f >= state.y) { break; }

        vec2 center = BufA(f).xy;
        center = ((center.xy / iResolution.xy) - vec2(0.25,0.5));
        center.x *= aspectRatio;

        control_points[i] = center;
    }


    float t = fract(iTime / SLOW_DOWN);

    if (int(iTime / SLOW_DOWN) % 2 == 1 && BACKWARDS_ANIMATION) {
        t = 1.0 - t;
    }

    // draw bezier curve using de Casteljau's algorithm
    if (SHOW_BLACK_LINE) {
        vec2 prev_point = control_points[0];

        float end_algorithm = t;

        if(ALWAYS_SHOW_BEZIER_CURVE) { end_algorithm = 1.0; }

        float increaser = end_algorithm / float(CASTELJAUS_STEPS);

        end_algorithm += 0.001; // without it some small values of CASTELJAUS_STEPS couses ending curve before endpoint
                                // (i don't know why)

        for (float i = 0.0; i <= end_algorithm; i += increaser) {
            float i2 = 1.0 - i;

            vec2 point = vec2(0.0);

            for (int point_index = 0; point_index < max_count; ++point_index) {
                point += float(BinomialCoefficient(max_count - 1, point_index)) 
                         * pow(i2, float(max_count - 1 - point_index)) 
                         * pow(i, float(point_index))
                         * control_points[point_index];
             }

            dist = DistanceToLineSegment(act_coord, prev_point, point) * 2.0;

            if (dist < EDGE + SMOOTH)
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
                color *= vec3(dist);
            }

            prev_point = point;
        }
    }

    // draw control points
    for (int i = 1; i < max_count - 1; ++i) {
        dist = SDFCircle(act_coord, control_points[i]);
        if (dist < EDGE + SMOOTH && SHOW_RED_DOTS)
        {
            dist = max(dist, 0.0);
            dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
            color *= mix(vec3(0.0,0.0, float(max_count - i) / float(max_count) ),vec3(1.0,1.0,1.0),dist);
        }
    }

    // draw start point
    dist = SDFCircle(act_coord, control_points[0]);
    if (dist < EDGE + SMOOTH && SHOW_RED_DOTS)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,1.0),dist);
    }

    // draw end point
    dist = SDFCircle(act_coord, control_points[max_count-1]);
    if (dist < EDGE + SMOOTH && SHOW_RED_DOTS)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color *= mix(vec3(1.0,0.0,0.0),vec3(1.0,1.0,1.0),dist);
    }

    // draw lines between control points
    if (SHOW_GREY_LINES) {
        for (int i = 0; i < max_count - 1; ++i) {
            dist = DistanceToLineSegment(act_coord, control_points[i], control_points[i + 1]) * 2.0;
            if (dist < EDGE + SMOOTH && SHOW_GREY_LINES)
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
                color *= mix(vec3(0.8),vec3(1.0,1.0,1.0),dist);
            }
        }
    }

    // draw support lines and points
    vec2 support_points[max_count] = control_points;

    for (int i = 0; i < max_count - 1; ++i) {
        support_points[0] = support_points[0] + t * (support_points[1] - support_points[0]);

        for (int j = 1; j < max_count - i - 1; ++j) {
            support_points[j] = support_points[j] + t * (support_points[j+1] - support_points[j]);

            dist = DistanceToLineSegment(act_coord, support_points[j-1], support_points[j]) * 2.0;
            if (dist < EDGE + SMOOTH && show_support_lines[i])
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
                color *= mix(support_lines_colors[i], vec3(1.0,1.0,1.0),dist);
            }

            dist = SDFCircle(act_coord, support_points[j-1]);
            if (dist < EDGE + SMOOTH && show_support_points[i])
            {
                dist = max(dist, 0.0);
                dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
                color = mix(support_points_colors[i], vec3(1.0,1.0,1.0),dist);
            }
        }

        dist = SDFCircle(act_coord, support_points[max_count - i - 1]);
        if (dist < EDGE + SMOOTH && i != 0  && show_support_points[i])
        {
            dist = max(dist, 0.0);
            dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
            color = mix(support_points_colors[i-1], vec3(1.0,1.0,1.0),dist);
        }
    }

    vec2 point = vec2(0.0);
    float t2 = (1.0 - t);

    for (int point_index = 0; point_index < max_count; ++point_index) {
        point += float(BinomialCoefficient(max_count - 1, point_index)) 
                 * pow(t2, float(max_count - 1 - point_index)) 
                 * pow(t, float(point_index))
                 * control_points[point_index];
     }

    // draw final point
    dist = SDFCircle(act_coord, point);
    if (dist < EDGE + SMOOTH && SHOW_FINAL_DOT)
    {
        dist = max(dist, 0.0);
        dist = smoothstep(EDGE,EDGE + SMOOTH,dist);
        color = mix(final_dot_color, vec3(1.0,1.0,1.0),dist);
    }


    fragColor = vec4(color,1.0);
}

