// this code is copied from https://www.shadertoy.com/view/4dtSRn (shader by mhnewman)

#define BEZIER_CURVE_POINTS 5 // int, minimum = 2, maximum = 13
// Remember to change BEZIER_CURVE_POINTS parameter in Image too! (second tab, you can select it ot the top of this window)
// After changing BEZIER_CURVE_POINTS peremeter reset shader's time (button on the bottom of shader window)

const int maxCount = BEZIER_CURVE_POINTS;
const float radius = 20.0;

const vec4 deleteRect = vec4(0.0, 0.0, 40.0, 40.0);

const float statePos = float(maxCount);
const float offsetPos = float(maxCount + 1);

#define addObject(xPos, yPos) { if (p.x == count) fragColor = vec4((xPos), (yPos), 0.0, 0.0); count += 1.0; }

bool inRect(vec2 pos, vec4 rect) {
    return pos.x >= rect.x && pos.y >= rect.y && pos.x < rect.z && pos.y < rect.w;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = fragCoord - 0.5;

    if (p.y > 0.0 || p.x > offsetPos) {
        fragColor = vec4(0.0);
        return;
    }

    if (iFrame == 0) {
        float count = 0.0;

        //
        // Initial conditions
        //
        addObject(0.2 * iResolution.x, 0.2 * iResolution.y);  // start point
        for(int i = 0; i < maxCount - 2; i++) {
            addObject((0.1 + (float(i) / float(maxCount))) * iResolution.x,
                      (0.8 - float(abs(i - maxCount / 2)) * 0.03) * iResolution.y); // control point
        }

        addObject(0.8 * iResolution.x, 0.2 * iResolution.y); // end point

        if (p.x == statePos)
            fragColor = vec4(2.0 * statePos, count, 0.0, 0.0);
        return;
    }

    vec4 state = texture(iChannel0, vec2(0.5 + statePos, 0.5) / iResolution.xy);
    vec4 offset = texture(iChannel0, vec2(0.5 + offsetPos, 0.5) / iResolution.xy);

    if (state.z <= 0.0 && iMouse.z > 0.0) {
        float r = radius + 1.0;
        for (int i = 0; i < maxCount; ++i) {
            float f = float(i);
            if (f >= state.y)
                break;
            vec4 center = texture(iChannel0, vec2(0.5 + f, 0.5) / iResolution.xy);
            float d = distance(iMouse.xy, center.xy);
            if (d < r * 5.0) {
                r = d;
                state.x = f;
                offset = center - iMouse;
            }
        }
        if (r > radius) {
            if (state.y >= statePos) {
            	state.x = 2.0 * statePos;
            } else {
    	        state.x = state.y;
        	    state.y += 1.0;
        	}
        }
    } 
    state.z = iMouse.z;

    if (p.x == statePos) {
        fragColor = state;
        return;
    }

    if (p.x == offsetPos) {
        fragColor = offset;
        return;
    }

    if (p.x == state.x && state.z > 0.0) {
        fragColor = iMouse + offset;
        return;
    }

    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

