Main {
    float h = U.y-START.y;
    float v = sqrt(2.*abs(h));
    Q = A(U);
    for (int x =-3;x<=3;x++)
    for (int y =-3;y<=3;y++)
    if (x!=0||y!=0)
    {
        vec2 u = vec2(x,y);
        float h = U.y+u.y-START.y;
        float uv = sqrt(2.*abs(h));
        float dx_dt = .5*uv+.5*v;
        vec4 a = A(U+u);
        float dx = length(u);
        float dt = dx/dx_dt;
        if (abs(dx_dt)<1e-9) dt = 1e9;
        if (a.z+dt<Q.z)
            Q.z = a.z+dt;

        if (length(a.xy-U)<length(Q.xy-U))
            Q.xy = a.xy;
    }
    float 
        n = A(Q.xy+vec2(0,1)).z,
        e = A(Q.xy+vec2(1,0)).z,
        s = A(Q.xy-vec2(0,1)).z,
        w = A(Q.xy-vec2(1,0)).z;
    vec2 g = vec2(e-w,n-s);
    if (length(g)>0.) {
        g = normalize(g);
        Q.xy += 2.5*g;
