const vec2  iFeedbackZoomCenter       = vec2(0., 0.);
const float iFeedbackZoomRate         = .001;
const float iFeedbackFadeRate         = .999;
const float iFeedbackColorShiftZoom   = .2;
const float iFeedbackColorShiftImpact = .004;
const vec2  iDrawCenter               = vec2(0., 0.);
const float iDrawIntensity            = .35;
const float iBlobEdgeSmoothing        = .04;
const float iBlob1Radius              = .3;
const float iBlob1PowFactor           = 40.;
const float iBlob1ColorPulseSpeed     = .07;
const float iBlob2Radius              = .4;
const float iBlob2PowFactor           = 40.;
const float iBlob2ColorPulseSpeed     = .07;
