void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = 6.0 / iResolution.y; // Approximately pixel size
    float transition = 0.5 + 0.5 * sin(iTime);

    // The grid is complete when skew=0.5 and stretch=sqrt(0.75)
    float skew = 0.5 * transition;
    uv.x /= mix(1.0, sqrt(0.75), transition); // Scale width to form equilateral triangles
    uv.y += skew * uv.x; // Skew vertically

    uv *= 2.0; // Zoom out to see more cells
    uv = fract(uv); // Subdivide into a grid

    float sTriId = step(0.0, uv.x - uv.y); // Upper left of the cell: 0, lower right of the cell: 1

    // Cell coloring show UVs and local IDs
    fragColor = mix(vec4(uv, 0.0, 1.0), vec4(sTriId, 0.0, 1.0 - sTriId, 1.0), transition);
    fragColor.rgb = mix(fragColor.rgb, vec3(1.0), smoothstep(unit, 0.0, abs(uv.y - uv.x)));
}

#define I 4.0*iMouse.x/iResolution.x - 2.0

#define a 0.7
#define b 0.8
#define tau 12.5

#define num_steps 200
#define init vec2(-1)
#define AA 1

const vec4 pi = vec4(1, 2, 4, 8)*atan(1.0);

const float eps = 0.3;

const bool displayNullclines = true;

// probably would be a better idea to store this in a buffer or something
vec2 curve[num_steps];

float sigmoid(float x){return 0.5*tanh(x) + 0.5;}

vec3 color(vec2 p){
    float red = sigmoid(p.y);
    float green = sigmoid(dot(vec2(0.707, -0.707), p));
    float blue = sigmoid(dot(vec2(-0.707, -0.707), p));
    float brightness = pow(length(p), 0.25);
    return vec3(red, green, blue)*brightness;
}

vec2 forwardComp(vec2 state, mat2 weights, int t, bool n){
    //return tanh(weights*state - bias) - state;
    float v = state.x;
    float w = state.y;
    return vec2(v - 0.333*v*v*v - w + I, (v + a - b*w)/tau);
}

vec2 dCurve(vec2 p){

    float dist = 9e9;
    float num = 0.0;

    for(int i = 0; i < num_steps - 1; i++){

        vec2 pos = p - curve[i];
        vec2 segment = curve[i + 1] - curve[i];
        float len = length(segment);
        vec2 normed = segment/len;
        vec2 ortho = vec2(normed.y, -normed.x);

        float proj1 = dot(normed, pos);
        float d = (proj1 < len && proj1 > 0.0)
                ? abs(dot(pos, ortho))
                : min(length(pos), distance(pos, segment));

        if (d < dist) {
            dist = d;
            num = float(i);
        }
    }
    return vec2(dist, num);
}

void eulerStep(vec2 p, mat2 weights){
    curve[0] = p;
    for(int i = 1; i < num_steps; i++){
        curve[i] = curve[i - 1] + eps*forwardComp(curve[i - 1], weights, i, true);
    }
}

vec3 render(vec2 frag, mat2 weights){

    vec2 xy = 3.0*(2.0*frag - iResolution.xy)/iResolution.y;
    //xy *= 8.0;

    vec2 flow = forwardComp(xy, weights, 0, false);

    if(displayNullclines){
        if(abs(flow.x) < 0.02) return vec3(0);
        if(abs(flow.y) < 0.001) return vec3(0);
    }

    //vec3 col = color(xy);
    vec3 col = color(flow);

    vec2 curve = dCurve(xy);
    float d = curve.x;
    float n = curve.y;
    if(d < 0.02) return col + vec3(0.6);
    else return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
