    return vec3(0.);
}

vec3 rdrHolo(vec2 uv)
{
    vec2 rep = vec2(.2);

     vec2 id = floor((uv+rep*.5)/rep);
    uv = mod(uv+rep*.5,rep)-rep*.5;
    vec2 l = abs(uv)-rep*.25;

    vec3 rgb = vec3(0.);

    rgb = mix(rgb, vec3(0.141,0.557,1.000), sat(sin((id.x+id.y)*.1+_time))-.5);
    rgb += vec3(1.000,0.396,0.141)*sat(sin((id.x-id.y)*1.+_time*2.)+sin(id.x));

    vec2 repid = vec2(40.,20);
    vec2 idscreen = floor((id+repid*.5)/repid);
    id = mod(id+repid*.5,repid)-repid*.75;

    vec2 screenl = abs(id)-repid*.4;
    float screen = max(screenl.x, screenl.y);

    rgb *= (1.-sat(screen*400.));

    if (mod(idscreen.x+idscreen.y, 2.0) < 0.1)
        rgb = rgb.zxy;

    return sat(rgb)*(1.-sat(max(l.x, l.y)*400.))*5.;
}

vec3 material(vec3 trace, vec3 ro, vec3 rd, vec3 p, vec3 n, vec2 uv)
{
    vec3 col = vec3(0.051,0.051,0.071);
    //col = n*.5+.5;
    if (trace.z == 2.)
    {
        col = p;
        col = rdrRoom(p, normalize(rd+(texture(iChannel1, uv*4.).xxx-.5)*.1));
        col += rdrHolo(p.xy-vec2(-_time,0.));
    }
    if (trace.z == 0.)
    {
        col += vec3(0.976,0.243,0.388)*pow(sat(dot(n, normalize(vec3(-1.)))),7.);
        col += vec3(0.976,0.427,0.243)*(1.-pow(sat(-dot(n, rd)),2.));
    }
    return col;
}



vec3 rdr(vec2 uv, vec2 ouv)
{
    vec3 col = vec3(0.);
    vec3 ro = vec3(2.,0.,-3.);
    vec3 ta = vec3(sin(_time*.5)*.1,sin(_time*.5+.5)*.1,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    vec3 res = trace(ro, rd, 300);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNormal(p, res.x);
        //l = n*.5+.5;
        //if (res.z == 0. || res.z == 2.)
        {
            col = material(res, ro, rd, p, n, uv);            
        }
        if (res.z == 1. || res.z == 2.)
        {
            vec3 reflro = p+n*0.01;
            float spec = 0.5;
            if (res.z == 2.)
            {
                float glassfreq = 20.;
                float glassSep = .9;
                float glassMult = 1.;
                float normMult = .75;
                n += normalize(n+normMult*vec3(sat((sin((p.x+_time)*glassfreq)-glassSep)*glassMult), sat((sin((p.y)*glassfreq)-glassSep)*glassMult), 0.));
                spec = 0.1;

                float rain = texture(iChannel1, (uv*r2d(-.25)*vec2(4.,.5)+vec2(0., mod(iTime,10.)))).x;
                col += pow(rain,15.)*vec3(800./iResolution.x);

                float rainB = texture(iChannel1, (uv*r2d(-.1)*vec2(4.,.25)*2.+vec2(0., mod(iTime*1.2,10.)))).x;
                col += pow(rainB,7.)*vec3(800./iResolution.x);
            }


            vec3 acc = vec3(0.);
            for (int ir = 0; ir < 1; ++ir)
            {
                        vec3 refl = normalize(reflect(rd, n));
                vec3 resrefl = trace(reflro, normalize(refl+(texture(iChannel1, vec2(float(ir)*0.1,float(ir)*.2)+uv*20.+mod(_time*0.1,100.)).xxx-.5)*spec), 256);
                if (resrefl.y > 0.)
                {
                    vec3 reflp = reflro+refl*resrefl.y;
                    vec3 refln = getNormal(reflp, resrefl.x);
                    acc += .15* material(resrefl, reflro, refl, reflp, refln, uv);
                    acc += dFdx(acc)*.5;
                    acc += dFdy(acc)*.5;

                }
                acc += .5*pow(texture(iChannel0, refl).xyz,vec3(1.))*.5+vec3(0.024,0.094,0.118)*.3;

            }
            col += acc;
        }
    }
    col += _accMarch*vec3(0.976,0.243,0.388);
    return col;
}

