#define scene(x) sceneMetaballs(x)
#define edgeLen 0.025

float voxelizeScene (vec3 p)
{
    float halo = scene(p) - edgeLen * 1.732;
    if ( halo >= 0.001 )
    {
        return halo;
    }
    else
    {
        vec3 tmpI;
        modf(p / edgeLen, tmpI);
        vec3 descP = (tmpI) * edgeLen;

        float retdist = edgeLen;
        for (int i = -1; i != 2; i++)
            for (int j = -1; j != 2; j++)
                for (int k = -1; k != 2; k++)
                {
                    descP = (tmpI + vec3(i,j,k)) * edgeLen;
                    float sceneEval = scene(descP);
                    if ( sceneEval < 0.0 ) retdist = min(sdBox(p - descP, edgeLen), retdist);
                }
        return retdist;
    }
}

bool sphereMarchScene(vec3 startPt, vec3 dir, out vec3 hitPt, out vec3 hitNorm)
{
    float i = 0.0;
    vec3 samplePt = startPt;
    for (i = 0.0; i != 120.0; i += 1.0)
    {
        float curDist = voxelizeScene (samplePt);
        if ( curDist < 0.001 )
        {
            hitNorm.x = voxelizeScene(samplePt + vec3(0.001, 0.0, 0.0)) - voxelizeScene(samplePt - vec3(0.001, 0.0, 0.0));
            hitNorm.y = voxelizeScene(samplePt + vec3(0.0, 0.001, 0.0)) - voxelizeScene(samplePt - vec3(0.0, 0.001, 0.0));
            hitNorm.z = voxelizeScene(samplePt + vec3(0.0, 0.0, 0.001)) - voxelizeScene(samplePt - vec3(0.0, 0.0, 0.001));
            hitNorm = normalize (hitNorm);
            hitPt = samplePt;
            return true;
        }
        samplePt += curDist * dir;
    }
    return false;
}

