// Autodiff utilities
vec2 fMul(in vec2 a, in vec2 b) { return vec2(a.x * b.x, a.x * b.y + a.y * b.x); }
vec2 fInv(in vec2 z) { float q = 1.0 / z.x; return vec2(q, -z.y * q / z.x); }
vec2 fPow(in vec2 a, in vec2 b) { float q = pow(a.x, b.x); return vec2(q, q * (b.x * a.y / a.x + b.y * log(a.x))); }
vec2 fIntPow(in vec2 z, in int power) { float res = 1.0; for (int n=0; n < abs(power); n++) { res *= z.x; } if (power < 0) res = 1.0 / res; return vec2(res, float(power) * res / z.x * z.y); }
vec2 fSqrt(in vec2 z) { float q = sqrt(z.x); return vec2(q, 0.5 * z.y / q); }
vec2 fSinh(in vec2 z) { return vec2(sinh(z.x), cosh(z.x) * z.y); }

// Constants
#define TAU 6.2831853071
#define E_INV 0.36787944117

float factorial(in float x) {
    x += 1.0;
    return sqrt(TAU / x) * pow(E_INV * x * sqrt(x * sinh(1.0 / x) + 1.0 / (810.0 * x * x * x * x * x * x)), x);
}

// Autodifferentiated version
vec2 factorial(in vec2 x) {
    x.x += 1.0;
    return fMul(fSqrt(TAU * fInv(x)), fPow(fMul(E_INV * x, fSqrt(fMul(x, fSinh(fInv(x))) + fInv(810.0 * fIntPow(x, 6)))), x));
}

